import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract product prices
    const productPrices = await page.$$eval('.product-card .product-price', elements => 
      elements.map(element => element.textContent.trim())
    );

    // Take screenshot after extracting prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_prices.png'));

    // Return the extracted data
    return {
      success: true,
      action: "Extracted all product prices successfully",
      extractedData: { prices: productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Best Sellers" category to navigate
    await page.waitForSelector('a:contains("Best Sellers")');
    await page.click('a:contains("Best Sellers")');
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to Best Sellers
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_best_sellers.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_best_sellers.png'));

    // Extract product prices
    const prices = await page.$$eval('.product-card .product-price', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Extract prices from the category cards, if present
    const categoryCardPrices = await page.$$eval('.category-card .product-price', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Combine both price arrays
    const allPrices = [...prices, ...categoryCardPrices];

    // Take screenshot of extracted prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_prices.png'));

    return {
      success: true,
      action: "Extracted product prices from Best Sellers and category cards",
      extractedData: { allPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

