import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the product cards to load
    await page.waitForSelector('.product-card');

    // Extract product prices
    const productPrices = await page.evaluate(() => {
      const prices = [];
      const priceElements = document.querySelectorAll('.product-card .product-price');
      priceElements.forEach(el => {
        prices.push(el.innerText.trim());
      });
      return prices;
    });

    // Log extracted prices
    console.log('Extracted Product Prices:', productPrices);

    // Take a screenshot after extracting prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_extracted.png'));

    return {
      success: true,
      action: "Extracted all product prices",
      extractedData: { productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for category links to load and click on each category
    const categoryLinksSelector = '.nav-links a';
    await page.waitForSelector(categoryLinksSelector);
    const categoryLinks = await page.$$(categoryLinksSelector);

    // Extract product prices from each category
    const productPrices = [];

    for (const link of categoryLinks) {
      const categoryUrl = await link.evaluate(el => el.href);
      await page.goto(categoryUrl);
      await page.waitForLoadState('networkidle');

      // Take screenshot of each category page
      await page.screenshot({ path: path.join(screenshotsDir, `category_page_${link.innerText}.png`) });
      screenshots.push(path.join(screenshotsDir, `category_page_${link.innerText}.png`));

      // Extract product prices
      const pricesSelector = '.product-card .product-price';
      await page.waitForSelector(pricesSelector);
      const prices = await page.$$eval(pricesSelector, priceElements => 
        priceElements.map(priceElement => priceElement.innerText.trim())
      );

      productPrices.push(...prices);
    }

    // Return the extracted data
    return {
      success: true,
      action: "Extracted product prices from all categories",
      extractedData: { productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the main content to load
    await page.waitForSelector('.nav-links');

    // Click on each category to extract product prices
    const categories = ['Best Sellers', 'New Releases', 'Electronics', 'Books', 'Clothing', 'Home & Kitchen'];
    for (const category of categories) {
      await page.click(`a:has-text("${category}")`);
      await page.waitForLoadState('networkidle');

      // Wait for product cards to appear
      await page.waitForSelector('.product-card');

      // Extract product prices
      const prices = await page.$$eval('.product-card .product-price', elements => 
        elements.map(el => el.textContent.trim())
      );

      // Log or store the extracted prices
      console.log(`Prices in ${category}:`, prices);

      // Take a screenshot of the current category view
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_${category.replace(/\s+/g, '_').toLowerCase()}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_${category.replace(/\s+/g, '_').toLowerCase()}.png`));

      // Navigate back to the home page to select the next category
      await page.goto(fileUrl);
      await page.waitForLoadState('networkidle');
    }

    return {
      success: true,
      action: "Extracted product prices from all categories",
      extractedData: {}, // Here you could include the collected prices if needed
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
