import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract product prices
    const productPrices = await page.$$eval('.product-card .product-price', prices => prices.map(price => price.innerText));

    // Log the extracted prices
    console.log('Extracted Product Prices:', productPrices);

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_prices.png'));

    return {
      success: true,
      action: "Extracted all product prices successfully",
      extractedData: { productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

