import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the category navigation to load
    await page.waitForSelector('.nav-links');

    // Extract product prices
    const productPrices = await page.$$eval('.product-card .product-price', elements => {
      return elements.map(el => el.innerText);
    });

    // Extract categories and their respective prices
    const categories = await page.$$eval('.category-card', elements => {
      return elements.map(el => ({
        categoryName: el.querySelector('h2') ? el.querySelector('h2').innerText : '',
        prices: Array.from(el.querySelectorAll('.product-price')).map(priceEl => priceEl.innerText)
      }));
    });

    // Take a screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted all product prices from categories",
      extractedData: {
        productPrices,
        categories
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
