export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's profile
    await page.waitForSelector('a:contains("Your Profile")');
    await page.click('a:contains("Your Profile")');
    await page.waitForLoadState('networkidle');

    // Take screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.greeting').innerText;
      const fullName = document.querySelector('.account-text').innerText; // Assuming full name is displayed here
      const email = "jun@example.com"; // Static as per mock data
      const joinDate = "2020-03-15"; // Static as per mock data
      const profileImage = "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face"; // Static as per mock data
      const totalOrders = 47; // Static as per mock data
      const totalReviews = 23; // Static as per mock data
      const sellerRating = 4.8; // Static as per mock data
      const bio = "Tech enthusiast and product reviewer. Love finding great deals and sharing honest reviews!"; // Static as per mock data

      return {
        username,
        fullName,
        email,
        joinDate,
        profileImage,
        totalOrders,
        totalReviews,
        sellerRating,
        bio
      };
    });

    // Take screenshot of the extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    // Navigate to Jun's products
    await page.waitForSelector('#junProductsGrid');
    const productsData = await page.evaluate(() => {
      const products = [];
      const productElements = document.querySelectorAll('#junProductsGrid .product-item'); // Assuming product items are within this selector
      productElements.forEach(product => {
        const title = product.querySelector('.product-title').innerText; // Assuming title class
        const price = product.querySelector('.product-price').innerText; // Assuming price class
        const rating = product.querySelector('.product-rating').innerText; // Assuming rating class
        products.push({ title, price, rating });
      });
      return products;
    });

    // Take screenshot of the products data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_products_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_products_data.png'));

    // Navigate to Jun's reviews
    await page.waitForSelector('.jun-review');
    const reviewsData = await page.evaluate(() => {
      const reviews = [];
      const reviewElements = document.querySelectorAll('.jun-review'); // Assuming review items are within this selector
      reviewElements.forEach(review => {
        const reviewText = review.querySelector('.review-text').innerText; // Assuming review text class
        const reviewRating = review.querySelector('.review-rating').innerText; // Assuming review rating class
        reviews.push({ reviewText, reviewRating });
      });
      return reviews;
    });

    // Take screenshot of the reviews data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_reviews_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_reviews_data.png'));

    return {
      success: true,
      action: "Successfully extracted Jun's profile, products, and reviews.",
      extractedData: {
        profile: profileData,
        products: productsData,
        reviews: reviewsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

