import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on "Your Profile" to navigate to Jun's profile
    await page.waitForSelector('a:contains("Your Profile")');
    await page.click('a:contains("Your Profile")');
    await page.waitForLoadState('networkidle');

    // Take screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract Jun's profile information
    const profileInfo = await page.evaluate(() => {
      const username = document.querySelector('.greeting')?.innerText;
      const totalOrders = document.querySelector('.profile-stats .orders')?.innerText;
      const totalReviews = document.querySelector('.profile-stats .reviews')?.innerText;
      const sellerRating = document.querySelector('.profile-stats .rating')?.innerText;
      const bio = document.querySelector('.bio')?.innerText;

      return {
        username,
        totalOrders,
        totalReviews,
        sellerRating,
        bio
      };
    });

    // Navigate to Jun's products
    await page.waitForSelector('#junProductsGrid');
    await page.click('#junProductsGrid');
    await page.waitForLoadState('networkidle');

    // Take screenshot of products page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products_page.png'));

    // Extract products information
    const products = await page.evaluate(() => {
      const productElements = Array.from(document.querySelectorAll('.product'));
      return productElements.map(product => {
        const title = product.querySelector('.product-title')?.innerText;
        const price = product.querySelector('.product-price')?.innerText;
        const rating = product.querySelector('.product-rating')?.innerText;
        const reviewCount = product.querySelector('.product-review-count')?.innerText;

        return { title, price, rating, reviewCount };
      });
    });

    // Navigate to Jun's reviews
    await page.waitForSelector('.jun-review');
    await page.click('.jun-review');
    await page.waitForLoadState('networkidle');

    // Take screenshot of reviews page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reviews_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reviews_page.png'));

    // Extract reviews information
    const reviews = await page.evaluate(() => {
      const reviewElements = Array.from(document.querySelectorAll('.review'));
      return reviewElements.map(review => {
        const reviewText = review.querySelector('.review-text')?.innerText;
        const reviewRating = review.querySelector('.review-rating')?.innerText;

        return { reviewText, reviewRating };
      });
    });

    // Compile extracted data
    const extractedData = {
      profileInfo,
      products,
      reviews
    };

    return {
      success: true,
      action: "Successfully harvested Jun's profile information, products, and reviews",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
