import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's profile
    await page.click('a:contains("Your Profile")');
    await page.waitForSelector('.profile-page');

    // Take screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      return {
        username: document.querySelector('.username').innerText,
        fullName: document.querySelector('.full-name').innerText,
        email: document.querySelector('.email').innerText,
        joinDate: document.querySelector('.join-date').innerText,
        totalOrders: document.querySelector('.total-orders').innerText,
        totalReviews: document.querySelector('.total-reviews').innerText,
        sellerRating: document.querySelector('.seller-rating').innerText,
        bio: document.querySelector('.bio').innerText,
      };
    });

    // Navigate to Jun's products
    await page.click('#junProductsGrid');
    await page.waitForSelector('.product-item');

    // Take screenshot of the products
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_jun_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_jun_products.png'));

    // Extract product information
    const productsData = await page.evaluate(() => {
      const products = [];
      const productElements = document.querySelectorAll('.product-item');
      productElements.forEach(product => {
        products.push({
          title: product.querySelector('.product-title').innerText,
          price: product.querySelector('.product-price').innerText,
          rating: product.querySelector('.product-rating').innerText,
          reviewCount: product.querySelector('.product-review-count').innerText,
        });
      });
      return products;
    });

    // Navigate to Jun's reviews
    await page.click('.jun-review');
    await page.waitForSelector('.review-item');

    // Take screenshot of the reviews
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_jun_reviews.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_jun_reviews.png'));

    // Extract reviews information
    const reviewsData = await page.evaluate(() => {
      const reviews = [];
      const reviewElements = document.querySelectorAll('.review-item');
      reviewElements.forEach(review => {
        reviews.push({
          productTitle: review.querySelector('.review-product-title').innerText,
          reviewText: review.querySelector('.review-text').innerText,
          rating: review.querySelector('.review-rating').innerText,
        });
      });
      return reviews;
    });

    return {
      success: true,
      action: "Successfully extracted Jun's profile, products, and reviews.",
      extractedData: {
        profile: profileData,
        products: productsData,
        reviews: reviewsData,
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

