import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's profile
    await page.click('a:contains("Your Profile")');
    await page.waitForSelector('.profile-page');

    // Take screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.username').innerText;
      const fullName = document.querySelector('.full-name').innerText;
      const email = document.querySelector('.email').innerText;
      const joinDate = document.querySelector('.join-date').innerText;
      const bio = document.querySelector('.bio').innerText;
      const totalOrders = document.querySelector('.total-orders').innerText;
      const totalReviews = document.querySelector('.total-reviews').innerText;
      const sellerRating = document.querySelector('.seller-rating').innerText;

      return {
        username,
        fullName,
        email,
        joinDate,
        bio,
        totalOrders,
        totalReviews,
        sellerRating
      };
    });

    // Take screenshot after extracting profile data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_profile_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_profile_data.png'));

    // Navigate to Jun's products
    await page.click('#junProductsGrid');
    await page.waitForSelector('.product-item');

    // Extract product information
    const productsData = await page.evaluate(() => {
      const products = Array.from(document.querySelectorAll('.product-item')).map(product => {
        const title = product.querySelector('.product-title').innerText;
        const price = product.querySelector('.product-price').innerText;
        const rating = product.querySelector('.product-rating').innerText;
        const reviewCount = product.querySelector('.product-review-count').innerText;

        return { title, price, rating, reviewCount };
      });
      return products;
    });

    // Take screenshot after extracting product data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_extracted_products_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_extracted_products_data.png'));

    // Navigate to Jun's reviews
    await page.click('.jun-review');
    await page.waitForSelector('.review-item');

    // Extract reviews information
    const reviewsData = await page.evaluate(() => {
      const reviews = Array.from(document.querySelectorAll('.review-item')).map(review => {
        const reviewText = review.querySelector('.review-text').innerText;
        const reviewRating = review.querySelector('.review-rating').innerText;

        return { reviewText, reviewRating };
      });
      return reviews;
    });

    // Take screenshot after extracting reviews data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_extracted_reviews_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_extracted_reviews_data.png'));

    return {
      success: true,
      action: "Successfully extracted Jun's profile, products, and reviews.",
      extractedData: {
        profileData,
        productsData,
        reviewsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

