        const reviewText = review.querySelector('.review-text').innerText;
        const reviewRating = parseFloat(review.querySelector('.review-rating').innerText);
        reviews.push({ reviewText, reviewRating });
      });
      return reviews;
    });

    return {
      success: true,
      action: "Extracted Jun's profile, products, and reviews successfully",
      extractedData: {
        profile: profileData,
        products: productsData,
        reviews: reviewsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

