import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's Profile
    await page.waitForSelector('a:contains("Your Profile")');
    await page.click('a:contains("Your Profile")');
    await page.waitForLoadState('networkidle');

    // Take screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.greeting').innerText;
      const totalOrders = document.querySelector('.profile-stats').innerText.match(/Total Orders: (\d+)/)[1];
      const totalReviews = document.querySelector('.profile-stats').innerText.match(/Total Reviews: (\d+)/)[1];
      const bio = document.querySelector('.bio').innerText;
      return { username, totalOrders, totalReviews, bio };
    });

    // Take screenshot of products
    await page.waitForSelector('#junProductsGrid');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products.png'));

    // Extract product information
    const products = await page.evaluate(() => {
      const productElements = Array.from(document.querySelectorAll('#junProductsGrid .product'));
      return productElements.map(product => ({
        title: product.querySelector('.product-title').innerText,
        price: product.querySelector('.product-price').innerText,
        rating: product.querySelector('.product-rating').innerText,
        reviewCount: product.querySelector('.product-reviews').innerText
      }));
    });

    // Take screenshot of reviews
    await page.waitForSelector('.jun-review');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reviews.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reviews.png'));

    // Extract reviews
    const reviews = await page.evaluate(() => {
      const reviewElements = Array.from(document.querySelectorAll('.jun-review'));
      return reviewElements.map(review => ({
        title: review.querySelector('.review-title').innerText,
        content: review.querySelector('.review-content').innerText,
        rating: review.querySelector('.review-rating').innerText
      }));
    });

    return {
      success: true,
      action: "Successfully extracted Jun's profile, products, and reviews",
      extractedData: { profileData, products, reviews },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
