      const totalReviews = 23; // Static as per mock data
      const sellerRating = 4.8; // Static as per mock data
      const bio = "Tech enthusiast and product reviewer. Love finding great deals and sharing honest reviews!"; // Static as per mock data

      return {
        username,
        fullName,
        email,
        joinDate,
        profileImage,
        totalOrders,
        totalReviews,
        sellerRating,
        bio
      };
    });

    // Take screenshot of the extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    // Navigate to Jun's products
    await page.waitForSelector('#junProductsGrid');
    const productsData = await page.evaluate(() => {
      const products = [];
      const productElements = document.querySelectorAll('#junProductsGrid .product-item'); // Assuming a structure for products
      productElements.forEach(product => {
        const title = product.querySelector('.product-title').innerText;
        const price = parseFloat(product.querySelector('.product-price').innerText.replace('$', ''));
        const rating = parseFloat(product.querySelector('.product-rating').innerText);
        const reviewCount = parseInt(product.querySelector('.product-review-count').innerText);
        products.push({ title, price, rating, reviewCount });
      });
      return products;
    });

    // Take screenshot of the products data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_products_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_products_data.png'));

    // Navigate to Jun's reviews
    await page.waitForSelector('.jun-review');
    const reviewsData = await page.evaluate(() => {
      const reviews = [];
      const reviewElements = document.querySelectorAll('.jun-review'); // Assuming a structure for reviews
      reviewElements.forEach(review => {
        const reviewText = review.querySelector('.review-text').innerText;
        const reviewRating = parseFloat(review.querySelector('.review-rating').innerText);
        reviews.push({ reviewText, reviewRating });
      });
      return reviews;
    });

    // Take screenshot of the reviews data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_reviews_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_reviews_data.png'));

    return {
      success: true,
      action: "Successfully harvested user profile information, products, and reviews.",
      extractedData: {
        profileData,
        productsData,
        reviewsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on "Your Profile" link
    await page.waitForSelector('a:contains("Your Profile")');
    await page.click('a:contains("Your Profile")');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile.png'));

    // Extract user profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.greeting').innerText.replace('Hello, ', '');
      const fullName = document.querySelector('.profile-name-selector')?.innerText || '';
      const email = document.querySelector('.profile-email-selector')?.innerText || '';
      const joinDate = document.querySelector('.profile-join-date-selector')?.innerText || '';
      const totalOrders = document.querySelector('.profile-orders-selector')?.innerText || '';
      const totalReviews = document.querySelector('.profile-reviews-selector')?.innerText || '';
      const sellerRating = document.querySelector('.profile-rating-selector')?.innerText || '';
      const bio = document.querySelector('.profile-bio-selector')?.innerText || '';

      return {
        username,
        fullName,
        email,
        joinDate,
        totalOrders,
        totalReviews,
        sellerRating,
        bio
      };
    });

    // Wait for product reviews section and extract reviews
    await page.waitForSelector('.jun-review');
    const reviewsData = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.jun-review')).map(review => ({
        reviewText: review.querySelector('.review-text-selector')?.innerText || '',
        reviewRating: review.querySelector('.review-rating-selector')?.innerText || '',
        reviewDate: review.querySelector('.review-date-selector')?.innerText || ''
      }));
    });

    // Wait for products section and extract products
    await page.waitForSelector('#junProductsGrid');
    const productsData = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('#junProductsGrid .product-item')).map(product => ({
        title: product.querySelector('.product-title-selector')?.innerText || '',
        price: product.querySelector('.product-price-selector')?.innerText || '',
        rating: product.querySelector('.product-rating-selector')?.innerText || '',
        inStock: product.querySelector('.product-stock-selector')?.innerText.includes('In Stock')
      }));
    });

    return {
      success: true,
      action: "Successfully extracted user profile, reviews, and product data",
      extractedData: {
        profile: profileData,
        reviews: reviewsData,
        products: productsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to User Profile
    await page.click('a:contains("Your Profile")');
    await page.waitForSelector('.profile-page', { timeout: 5000 });

    // Take screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract User Profile Information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.greeting').innerText.replace('Hello, ', '');
      const fullName = document.querySelector('.profile-name').innerText;
      const email = document.querySelector('.profile-email').innerText;
      const joinDate = document.querySelector('.join-date').innerText;
      const bio = document.querySelector('.profile-bio').innerText;

      return { username, fullName, email, joinDate, bio };
    });

    // Navigate to Jun's Products
    await page.click('#junProductsGrid');
    await page.waitForSelector('.product-item', { timeout: 5000 });

    // Extract Jun's Products
    const productsData = await page.evaluate(() => {
      const products = [];
      document.querySelectorAll('.product-item').forEach(item => {
        const title = item.querySelector('.product-title').innerText;
        const price = item.querySelector('.product-price').innerText;
        const rating = item.querySelector('.product-rating').innerText;
        products.push({ title, price, rating });
      });
      return products;
    });

    // Navigate to Jun's Reviews
    await page.click('.jun-review');
    await page.waitForSelector('.review-item', { timeout: 5000 });

    // Extract Jun's Reviews
    const reviewsData = await page.evaluate(() => {
      const reviews = [];
      document.querySelectorAll('.review-item').forEach(item => {
        const reviewTitle = item.querySelector('.review-title').innerText;
        const reviewContent = item.querySelector('.review-content').innerText;
        const reviewRating = item.querySelector('.review-rating').innerText;
        reviews.push({ reviewTitle, reviewContent, reviewRating });
      });
      return reviews;
    });

    // Combine all extracted data
    const extractedData = {
      profile: profileData,
      products: productsData,
      reviews: reviewsData
    };

    return {
      success: true,
      action: "Successfully extracted user profile, products, and reviews.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
