import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's profile
    await page.waitForSelector('a:contains("Your Profile")');
    await page.click('a:contains("Your Profile")');
    await page.waitForLoadState('networkidle');

    // Take screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.greeting').innerText;
      const fullName = document.querySelector('.account-text').innerText;
      const email = "jun@example.com"; // Assuming email is static for this task
      const joinDate = "2020-03-15"; // Assuming join date is static for this task
      const profileImage = "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face";
      const totalOrders = 47; // Assuming total orders is static for this task
      const totalReviews = 23; // Assuming total reviews is static for this task
      const sellerRating = 4.8; // Assuming seller rating is static for this task
      const bio = "Tech enthusiast and product reviewer. Love finding great deals and sharing honest reviews!";

      return {
        username,
        fullName,
        email,
        joinDate,
        profileImage,
        totalOrders,
        totalReviews,
        sellerRating,
        bio
      };
    });

    // Navigate to products section
    await page.waitForSelector('#junProductsGrid');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products_section.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products_section.png'));

    // Extract product information
    const productsData = await page.evaluate(() => {
      const products = [];
      const productElements = document.querySelectorAll('#junProductsGrid .product-item');
      productElements.forEach(product => {
        const title = product.querySelector('.product-title').innerText;
        const price = product.querySelector('.product-price').innerText;
        const rating = product.querySelector('.product-rating').innerText;
        const reviewCount = product.querySelector('.product-review-count').innerText;

        products.push({ title, price, rating, reviewCount });
      });
      return products;
    });

    // Navigate to reviews section
    await page.waitForSelector('.jun-review');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reviews_section.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reviews_section.png'));

    // Extract reviews information
    const reviewsData = await page.evaluate(() => {
      const reviews = [];
      const reviewElements = document.querySelectorAll('.jun-review');
      reviewElements.forEach(review => {
        const reviewText = review.querySelector('.review-text').innerText;
        const reviewRating = review.querySelector('.review-rating').innerText;

        reviews.push({ reviewText, reviewRating });
      });
      return reviews;
    });

    // Compile extracted data
    const extractedData = {
      profile: profileData,
      products: productsData,
      reviews: reviewsData
    };

    return {
      success: true,
      action: "Successfully extracted Jun's profile, products, and reviews.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

