import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the account dropdown to access "Your Profile"
    await page.waitForSelector('.account-lists', { timeout: 5000 });
    await page.hover('.account-lists');
    await page.waitForSelector('a:has-text("Your Profile")', { timeout: 3000 });

    // Click "Your Profile" link
    const profileLink = await page.$('a:has-text("Your Profile")');
    if (profileLink) {
      await profileLink.click();
    } else {
      throw new Error('Could not find "Your Profile" link');
    }

    // Wait for profile page to load (look for .profile-page)
    await page.waitForSelector('.profile-page', { timeout: 5000 });

    // Screenshot profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile info
    const profileData = await page.evaluate(() => {
      const data = {};
      const profile = document.querySelector('.profile-page');
      if (!profile) return data;

      // Username
      const username = profile.querySelector('.profile-username')?.textContent?.trim() || null;
      // Full name
      const fullName = profile.querySelector('.profile-fullname')?.textContent?.trim() || null;
      // Email
      const email = profile.querySelector('.profile-email')?.textContent?.trim() || null;
      // Join date
      const joinDate = profile.querySelector('.profile-joindate')?.textContent?.trim() || null;
      // Profile image
      const profileImage = profile.querySelector('.profile-image img')?.src || null;
      // Bio
      const bio = profile.querySelector('.profile-bio')?.textContent?.trim() || null;

      data.username = username;
      data.fullName = fullName;
      data.email = email;
      data.joinDate = joinDate;
      data.profileImage = profileImage;
      data.bio = bio;

      return data;
    });

    // Extract profile stats
    await page.waitForSelector('.profile-stats', { timeout: 2000 });
    const profileStats = await page.evaluate(() => {
      const stats = {};
      const statsElem = document.querySelector('.profile-stats');
      if (!statsElem) return stats;
      // Example: <div class="profile-stats"><span class="orders">Orders: 47</span> ... </div>
      const orders = statsElem.querySelector('.orders')?.textContent?.match(/\d+/)?.[0];
      const reviews = statsElem.querySelector('.reviews')?.textContent?.match(/\d+/)?.[0];
      const sellerRating = statsElem.querySelector('.seller-rating')?.textContent?.match(/[\d.]+/)?.[0];
      stats.totalOrders = orders ? parseInt(orders, 10) : null;
      stats.totalReviews = reviews ? parseInt(reviews, 10) : null;
      stats.sellerRating = sellerRating ? parseFloat(sellerRating) : null;
      return stats;
    });

    // Scroll to products grid
    await page.waitForSelector('#junProductsGrid', { timeout: 3000 });
    await page.evaluate(() => {
      document.querySelector('#junProductsGrid').scrollIntoView({ behavior: 'smooth' });
    });
    await page.waitForTimeout(500);

    // Screenshot products grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products_grid.png'));

    // Extract Jun's products
    const products = await page.evaluate(() => {
      const grid = document.querySelector('#junProductsGrid');
      if (!grid) return [];
      const productCards = grid.querySelectorAll('.jun-product');
      return Array.from(productCards).map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim() || null,
        price: card.querySelector('.product-price')?.textContent?.trim() || null,
        rating: card.querySelector('.product-rating')?.textContent?.trim() || null,
        image: card.querySelector('img')?.src || null,
        description: card.querySelector('.product-description')?.textContent?.trim() || null
      }));
    });

    // Scroll to reviews section
    await page.waitForSelector('.jun-review', { timeout: 3000 });
    await page.evaluate(() => {
      const review = document.querySelector('.jun-review');
      if (review) review.scrollIntoView({ behavior: 'smooth' });
    });
    await page.waitForTimeout(500);

    // Screenshot reviews section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reviews.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reviews.png'));

    // Extract Jun's reviews
    const reviews = await page.evaluate(() => {
      const reviewElems = document.querySelectorAll('.jun-review');
      return Array.from(reviewElems).map(r => ({
        product: r.querySelector('.review-product')?.textContent?.trim() || null,
        rating: r.querySelector('.review-rating')?.textContent?.trim() || null,
        date: r.querySelector('.review-date')?.textContent?.trim() || null,
        content: r.querySelector('.review-content')?.textContent?.trim() || null
      }));
    });

    // Compose extracted data
    const extractedData = {
      profile: profileData,
      stats: profileStats,
      products,
      reviews
    };

    return {
      success: true,
      action: "Extracted all available information about Jun from his profile, products, and reviews for profiling purposes.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1Path = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1Path });
    screenshots.push(step1Path);

    // 1. Open "Your Profile" via top nav dropdown (using text selector and forcing mouse hover)
    const accountListsSelector = '.account-lists';
    await page.waitForSelector(accountListsSelector, { timeout: 6000 });

    // Hover to reveal dropdown
    await page.hover(accountListsSelector);

    // Wait for dropdown to appear (alternative selector: h3:text("Your Account") ~ a:text("Your Profile"))
    const yourProfileLink = await page.waitForSelector('a:has-text("Your Profile")', { timeout: 4000 });

    // Click "Your Profile" to open profile page
    await yourProfileLink.click();

    // Wait for profile page to load (look for .profile-page or Jun's name)
    await page.waitForSelector('.profile-page, text=Jun Kim', { timeout: 7000 });

    // Screenshot of profile page
    const step2Path = path.join(screenshotsDir, 'step_2_jun_profile.png');
    await page.screenshot({ path: step2Path });
    screenshots.push(step2Path);

    // 2. Extract Jun's profile info (using different selectors: .profile-page, xpath, and text)
    // Try .profile-page first, fallback to text selectors if needed
    let profileSection = await page.$('.profile-page');
    if (!profileSection) {
      // Try xpath as backup
      profileSection = await page.$('//div[contains(@class,"profile-page") or contains(text(),"Jun Kim")]');
    }

    // Extract profile fields
    const extractedData = {};
    // Username
    extractedData.username = await page.textContent('.profile-page .username, text=jun', { timeout: 2000 }).catch(() => null);

    // Full Name
    extractedData.fullName = await page.textContent('.profile-page .full-name, text=Jun Kim', { timeout: 2000 }).catch(() => null);

    // Email
    extractedData.email = await page.textContent('.profile-page .email, text=jun@example.com', { timeout: 2000 }).catch(() => null);

    // Join Date
    extractedData.joinDate = await page.textContent('.profile-page .join-date, text=2020-03-15', { timeout: 2000 }).catch(() => null);

    // Profile Image
    let img = await page.$('.profile-page img');
    extractedData.profileImage = img ? await img.getAttribute('src') : null;

    // Bio
    extractedData.bio = await page.textContent('.profile-page .profile-bio, .profile-page .bio', { timeout: 2000 }).catch(() => null);

    // 3. Profile stats (orders, reviews, seller rating)
    // Use .profile-stats, fallback to numerical text search
    const stats = {};
    stats.totalOrders = await page.textContent('.profile-stats .orders, text=/\\d+ Orders/i', { timeout: 2000 }).catch(() => null);
    stats.totalReviews = await page.textContent('.profile-stats .reviews, text=/\\d+ Reviews/i', { timeout: 2000 }).catch(() => null);
    stats.sellerRating = await page.textContent('.profile-stats .seller-rating, text=/[0-9.]+\\s*stars?/i', { timeout: 2000 }).catch(() => null);
    extractedData.stats = stats;

    // 4. Products grid (#junProductsGrid)
    // Wait for products grid
    await page.waitForSelector('#junProductsGrid', { timeout: 5000 });
    // Extract product cards (use .product-card or .jun-product as alternative)
    const productCards = await page.$$('#junProductsGrid .product-card, #junProductsGrid .jun-product, #junProductsGrid > div');
    extractedData.products = [];
    for (const card of productCards) {
      const title = await card.textContent('.product-title, h3, .title').catch(() => null);
      const price = await card.textContent('.product-price, .price, text=/\\$[0-9.,]+/').catch(() => null);
      const rating = await card.textContent('.product-rating, .rating, text=/[0-9.]+\\s*stars?/i').catch(() => null);
      const imgEl = await card.$('img');
      const image = imgEl ? await imgEl.getAttribute('src') : null;
      extractedData.products.push({ title, price, rating, image });
    }

    // Screenshot of profile products grid
    const step3Path = path.join(screenshotsDir, 'step_3_jun_products.png');
    await page.screenshot({ path: step3Path });
    screenshots.push(step3Path);

    // 5. Jun's reviews (.jun-review)
    // Wait for at least one review
    await page.waitForSelector('.jun-review', { timeout: 5000 });
    const reviews = await page.$$('.jun-review');
    extractedData.reviews = [];
    for (const review of reviews) {
      const product = await review.textContent('.review-product, .review-title, .product-title').catch(() => null);
      const stars = await review.textContent('.review-stars, .stars, text=/[0-9.]+\\s*stars?/i').catch(() => null);
      const reviewText = await review.textContent('.review-text, .text, p').catch(() => null);
      const date = await review.textContent('.review-date, .date, text=/\\d{4}-\\d{2}-\\d{2}/').catch(() => null);
      extractedData.reviews.push({ product, stars, reviewText, date });
    }

    // Screenshot of reviews
    const step4Path = path.join(screenshotsDir, 'step_4_jun_reviews.png');
    await page.screenshot({ path: step4Path });
    screenshots.push(step4Path);

    return {
      success: true,
      action: "Harvested Jun's user profile info, products, and reviews using a variety of selectors and strategies.",
      extractedData,
      screenshots,
      error: null
    };

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // 1. Open the "Account & Lists" dropdown by hovering (to reveal profile link)
    await page.waitForSelector('.account-lists', { timeout: 5000 });
    await page.hover('.account-lists');
    await page.waitForSelector('.dropdown-content a', { timeout: 5000 });

    // 2. Click "Your Profile" (using :text and partial text matching for robustness)
    const profileLink = await page.locator('.dropdown-content a', { hasText: 'Your Profile' }).first();
    await profileLink.click();

    // Wait for the profile page to load â€“ look for .profile-page or .profile-stats
    await page.waitForSelector('.profile-page, .profile-stats, text=Jun', { timeout: 5000 });

    // Take screenshot after navigating to profile
    const profileScreenshot = path.join(screenshotsDir, 'step_2_profile.png');
    await page.screenshot({ path: profileScreenshot });
    screenshots.push(profileScreenshot);

    // 3. Extract profile info: username, full name, email, join date, profile image, bio, stats
    // Try to grab info from both .profile-page and .profile-stats using multiple strategies

    // Username (try to get from greeting or profile header)
    let username = null;
    try {
      username = await page.locator('.profile-page .username, .profile-stats .username, text=/jun/i').first().innerText();
    } catch {}

    // Full Name
    let fullName = null;
    try {
      fullName = await page.locator('.profile-page .full-name, .profile-stats .full-name, text=/Jun Kim/i').first().innerText();
    } catch {}

    // Email
    let email = null;
    try {
      email = await page.locator('.profile-page .email, .profile-stats .email, text=/@/i').first().innerText();
    } catch {}

    // Join Date
    let joinDate = null;
    try {
      joinDate = await page.locator('.profile-page .join-date, .profile-stats .join-date, text=/Joined|Join Date/i').first().innerText();
    } catch {}

    // Profile Image
    let profileImage = null;
    try {
      profileImage = await page.locator('.profile-page img, .profile-stats img').first().getAttribute('src');
    } catch {}

    // Bio
    let bio = null;
    try {
      bio = await page.locator('.profile-page .bio, .profile-stats .bio').first().innerText();
    } catch {}

    // Stats: totalOrders, totalReviews, sellerRating (try text and .profile-stats)
    let totalOrders = null, totalReviews = null, sellerRating = null;
    try {
      const statsText = await page.locator('.profile-stats').first().innerText();
      totalOrders = statsText.match(/(\d+)\s+Orders/i)?.[1] || null;
      totalReviews = statsText.match(/(\d+)\s+Reviews/i)?.[1] || null;
      sellerRating = statsText.match(/([0-9.]+)\s*\/\s*5|Seller Rating:\s*([0-9.]+)/i)?.[1] || null;
    } catch {}

    // 4. Extract Jun's products grid
    await page.waitForSelector('#junProductsGrid', { timeout: 5000 });
    const productCards = await page.$$('#junProductsGrid .product-card, #junProductsGrid .product, #junProductsGrid > div');
    let products = [];
    for (let i = 0; i < Math.min(3, productCards.length); ++i) {
      const card = productCards[i];
      const title = await card.$eval('.product-title, .title', el => el.innerText).catch(() => null);
      const price = await card.$eval('.product-price, .price', el => el.innerText).catch(() => null);
      const image = await card.$eval('img', el => el.src).catch(() => null);
      products.push({ title, price, image });
    }

    // Screenshot of products grid
    const productsScreenshot = path.join(screenshotsDir, 'step_3_products.png');
    await page.screenshot({ path: productsScreenshot });
    screenshots.push(productsScreenshot);

    // 5. Extract Jun's reviews
    // Wait for .jun-review, try to extract at least 2 reviews (text, rating, product)
    await page.waitForSelector('.jun-review', { timeout: 5000 });
    const reviewNodes = await page.$$('.jun-review');
    let reviews = [];
    for (let i = 0; i < Math.min(2, reviewNodes.length); ++i) {
      const r = reviewNodes[i];
      const product = await r.$eval('.review-product, .product-title', el => el.innerText).catch(() => null);
      const rating = await r.$eval('.review-rating, .star-rating', el => el.innerText).catch(() => null);
      const reviewText = await r.$eval('.review-text, .text', el => el.innerText).catch(() => null);
      reviews.push({ product, rating, reviewText });
    }

    // Screenshot of reviews
    const reviewsScreenshot = path.join(screenshotsDir, 'step_4_reviews.png');
    await page.screenshot({ path: reviewsScreenshot });
    screenshots.push(reviewsScreenshot);

    // Collate extracted data
    const extractedData = {
      profile: {
        username,
        fullName,
        email,
        joinDate,
        profileImage,
        bio,
        totalOrders,
        totalReviews,
        sellerRating
      },
      products,
      reviews
    };

    return {
      success: true,
      action: "Extracted Jun's complete profile, product and review information from profile page using multiple selectors and interaction strategies.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
