import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Open Jun's Profile Page
    // Try to find "Your Profile" link in header dropdown and click it
    // Hover over Account & Lists to reveal dropdown
    const accountListsSelector = '.account-lists';
    await page.waitForSelector(accountListsSelector, { timeout: 5000 });
    await page.hover(accountListsSelector);

    // Wait for dropdown to appear and click "Your Profile"
    const profileLinkSelector = 'a:has-text("Your Profile")';
    await page.waitForSelector(profileLinkSelector, { timeout: 3000 });
    await page.click(profileLinkSelector);

    // Wait for profile page to load (look for .profile-page)
    await page.waitForSelector('.profile-page', { timeout: 5000 });

    // Screenshot profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // 2. Extract Jun's Profile Information
    const profileData = await page.evaluate(() => {
      const profile = document.querySelector('.profile-page');
      if (!profile) return null;

      // Username
      const username = profile.querySelector('.profile-username')?.textContent?.trim() || null;
      // Full name
      const fullName = profile.querySelector('.profile-fullname')?.textContent?.trim() || null;
      // Email
      const email = profile.querySelector('.profile-email')?.textContent?.trim() || null;
      // Join date
      const joinDate = profile.querySelector('.profile-joindate')?.textContent?.trim() || null;
      // Profile image
      const profileImage = profile.querySelector('.profile-avatar img')?.src || null;
      // Bio
      const bio = profile.querySelector('.profile-bio')?.textContent?.trim() || null;

      // Stats
      const stats = {};
      profile.querySelectorAll('.profile-stats .stat').forEach(stat => {
        const label = stat.querySelector('.stat-label')?.textContent?.trim().toLowerCase();
        const value = stat.querySelector('.stat-value')?.textContent?.trim();
        if (label && value) stats[label] = value;
      });

      return {
        username,
        fullName,
        email,
        joinDate,
        profileImage,
        bio,
        stats
      };
    });

    // 3. Extract Jun's Products
    // Wait for products grid
    await page.waitForSelector('#junProductsGrid', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products.png'));

    const products = await page.evaluate(() => {
      const grid = document.querySelector('#junProductsGrid');
      if (!grid) return [];
      return Array.from(grid.querySelectorAll('.product-card')).map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim() || null,
        price: card.querySelector('.product-price')?.textContent?.trim() || null,
        category: card.querySelector('.product-category')?.textContent?.trim() || null,
        rating: card.querySelector('.product-rating')?.textContent?.trim() || null,
        image: card.querySelector('img')?.src || null
      }));
    });

    // 4. Extract Jun's Reviews
    // Wait for reviews section
    await page.waitForSelector('.jun-review', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_reviews.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_reviews.png'));

    const reviews = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.jun-review')).map(review => ({
        product: review.querySelector('.review-product-title')?.textContent?.trim() || null,
        rating: review.querySelector('.review-rating')?.textContent?.trim() || null,
        date: review.querySelector('.review-date')?.textContent?.trim() || null,
        text: review.querySelector('.review-text')?.textContent?.trim() || null
      }));
    });

    // Compose extracted data
    const extractedData = {
      profile: profileData,
      products,
      reviews
    };

    return {
      success: true,
      action: "Extracted Jun's full profile, products, and reviews from the Amazon simulation site.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

