    const profileLinkSelector = 'a:has-text("Your Profile")';
    await page.waitForSelector(profileLinkSelector, { timeout: 3000 });

    // 2. Click "Your Profile" link to open Jun's profile page (uses text selector)
    await page.click(profileLinkSelector);
    await page.waitForTimeout(800); // Wait for profile page content to load (simulate dynamic content)

    // Take screenshot of profile page
    const step2 = path.join(screenshotsDir, 'step_2_profile_opened.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

