    await page.waitForSelector(profilePageSelector, { timeout: 4000 });

    // 4. Extract Jun's profile information using multiple strategies
    // Try to extract using both class and text content
    const extractedData = {};

    // Use evaluate for bulk extraction
    const profileData = await page.evaluate(() => {
      // Try to find elements by class or semantic selectors
      let username = '';
      let fullName = '';
      let email = '';
      let joinDate = '';
      let bio = '';
      let profileImage = '';
      let stats = {};

      // Username or fullName
      const nameEl = document.querySelector('.profile-page .profile-username, .profile-page .profile-fullname, .profile-header .profile-username, .profile-header .profile-fullname, [data-testid="profile-fullname"]');
      if (nameEl) {
        fullName = nameEl.textContent.trim();
      } else {
        // Try to find by strong or h2
        const altName = document.querySelector('.profile-page strong, .profile-page h2, .profile-header strong, .profile-header h2');
        if (altName) fullName = altName.textContent.trim();
      }

      // Username (fallback)
      const unameEl = document.querySelector('.profile-page .profile-username, [data-testid="profile-username"]');
      if (unameEl) username = unameEl.textContent.trim();

      // Email
      const emailEl = document.querySelector('.profile-page .profile-email, [data-testid="profile-email"]');
      if (emailEl) email = emailEl.textContent.trim();

      // Join date
      const joinEl = document.querySelector('.profile-page .profile-join-date, [data-testid="profile-join-date"], .profile-header .profile-join-date');
      if (joinEl) joinDate = joinEl.textContent.replace(/Joined:?/i, '').trim();

