import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Hover over account dropdown to reveal profile link
    await page.hover('.account-lists');
    await page.waitForSelector('.dropdown-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_account_dropdown.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_account_dropdown.png'));

    // Click on Your Profile link
    await page.click('a:has-text("Your Profile")');
    await page.waitForSelector('.profile-page');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const profile = {};

      // Basic profile info
      profile.username = document.querySelector('.profile-username')?.innerText || 'N/A';
      profile.fullName = document.querySelector('.profile-fullname')?.innerText || 'N/A';
      profile.email = document.querySelector('.profile-email')?.innerText || 'N/A';
      profile.joinDate = document.querySelector('.profile-joindate')?.innerText || 'N/A';
      profile.bio = document.querySelector('.profile-bio')?.innerText || 'N/A';
      profile.profileImage = document.querySelector('.profile-image')?.src || 'N/A';

      // Profile stats
      const stats = {};
      const statElements = document.querySelectorAll('.profile-stats .stat-item');
      statElements.forEach(stat => {
        const label = stat.querySelector('.stat-label')?.innerText;
        const value = stat.querySelector('.stat-value')?.innerText;
        if (label && value) stats[label] = value;
      });
      profile.stats = stats;

      // Reviews
      profile.reviews = [];
      const reviewElements = document.querySelectorAll('.jun-review');
      reviewElements.forEach(review => {
        const reviewData = {
          product: review.querySelector('.review-product')?.innerText,
          rating: review.querySelector('.review-rating')?.innerText,
          date: review.querySelector('.review-date')?.innerText,
          text: review.querySelector('.review-text')?.innerText
        };
        profile.reviews.push(reviewData);
      });

      // Products
      profile.products = [];
      const productElements = document.querySelectorAll('#junProductsGrid .product-item');
      productElements.forEach(product => {
        const productData = {
          title: product.querySelector('.product-title')?.innerText,
          price: product.querySelector('.product-price')?.innerText,
          rating: product.querySelector('.product-rating')?.innerText,
          image: product.querySelector('.product-image')?.src,
          category: product.querySelector('.product-category')?.innerText
        };
        profile.products.push(productData);
      });

      return profile;
    });

    // Take final screenshot with extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_profile_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_profile_data.png'));

    return {
      success: true,
      action: "Successfully extracted Jun's profile information including personal details, statistics, reviews, and products",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Jun's profile using the account dropdown
    await page.hover('.account-lists');
    await page.waitForTimeout(1000);
    await page.click('a:has-text("Your Profile")');

    // Wait for profile page to load
    await page.waitForSelector('.profile-page', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const profile = {};

      // Basic profile info
      profile.username = document.querySelector('.profile-username')?.textContent?.trim();
      profile.fullName = document.querySelector('.profile-fullname')?.textContent?.trim();
      profile.email = document.querySelector('.profile-email')?.textContent?.trim();
      profile.joinDate = document.querySelector('.profile-joindate')?.textContent?.trim();
      profile.bio = document.querySelector('.profile-bio')?.textContent?.trim();

      // Profile stats
      const stats = document.querySelectorAll('.profile-stats .stat-item');
      profile.stats = {};
      stats.forEach(stat => {
        const label = stat.querySelector('.stat-label')?.textContent?.trim();
        const value = stat.querySelector('.stat-value')?.textContent?.trim();
        if (label && value) profile.stats[label] = value;
      });

      return profile;
    });

    // Extract products information
    const productsData = await page.$$eval('#junProductsGrid .product-item', products => 
      products.map(product => ({
        title: product.querySelector('.product-title')?.textContent?.trim(),
        price: product.querySelector('.product-price')?.textContent?.trim(),
        rating: product.querySelector('.product-rating')?.textContent?.trim(),
        description: product.querySelector('.product-description')?.textContent?.trim(),
        image: product.querySelector('.product-image')?.src
      }))
    );

    // Extract reviews information
    const reviewsData = await page.$$eval('.jun-review', reviews => 
      reviews.map(review => ({
        product: review.querySelector('.review-product')?.textContent?.trim(),
        rating: review.querySelector('.review-rating')?.textContent?.trim(),
        title: review.querySelector('.review-title')?.textContent?.trim(),
        content: review.querySelector('.review-content')?.textContent?.trim(),
        date: review.querySelector('.review-date')?.textContent?.trim()
      }))
    );

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Successfully extracted Jun's profile information, products, and reviews",
      extractedData: {
        profile: profileData,
        products: productsData,
        reviews: reviewsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on account dropdown to reveal profile options
    await page.waitForSelector('.account-lists', { timeout: 5000 });
    await page.click('.account-lists');

    // Wait for dropdown to appear and click on profile
    await page.waitForSelector('a:has-text("Your Profile")', { timeout: 3000 });
    await page.click('a:has-text("Your Profile")');

    // Wait for profile page to load
    await page.waitForSelector('.profile-page', { timeout: 5000 });

    // Take screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const profile = {};

      // Extract basic profile info
      const profileEl = document.querySelector('.profile-page');
      if (profileEl) {
        profile.name = profileEl.querySelector('.profile-name')?.innerText;
        profile.email = profileEl.querySelector('.profile-email')?.innerText;
        profile.joinDate = profileEl.querySelector('.join-date')?.innerText;
        profile.bio = profileEl.querySelector('.profile-bio')?.innerText;
      }

      // Extract profile stats
      const statsEl = document.querySelector('.profile-stats');
      if (statsEl) {
        profile.totalOrders = statsEl.querySelector('.total-orders')?.innerText;
        profile.totalReviews = statsEl.querySelector('.total-reviews')?.innerText;
        profile.sellerRating = statsEl.querySelector('.seller-rating')?.innerText;
      }

      // Extract products
      profile.products = [];
      const productGrid = document.querySelector('#junProductsGrid');
      if (productGrid) {
        const products = productGrid.querySelectorAll('.product-item');
        products.forEach(product => {
          profile.products.push({
            title: product.querySelector('.product-title')?.innerText,
            price: product.querySelector('.product-price')?.innerText,
            rating: product.querySelector('.product-rating')?.innerText
          });
        });
      }

      // Extract reviews
      profile.reviews = [];
      const reviews = document.querySelectorAll('.jun-review');
      reviews.forEach(review => {
        profile.reviews.push({
          product: review.querySelector('.review-product')?.innerText,
          rating: review.querySelector('.review-rating')?.innerText,
          text: review.querySelector('.review-text')?.innerText,
          date: review.querySelector('.review-date')?.innerText
        });
      });

      return profile;
    });

    // Take final screenshot with all data visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_data.png'));

    return {
      success: true,
      action: "Successfully extracted Jun's profile information, products, and reviews",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
