import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product grid/list to appear (assuming .add-to-cart exists in DOM)
    await page.waitForSelector('.add-to-cart, button:has-text("Add to Cart"), //button[contains(text(),"Add to Cart")]', { timeout: 5000 });

    // Find all add-to-cart buttons (try multiple strategies)
    let addToCartButtons = await page.$$('.add-to-cart');
    if (addToCartButtons.length === 0) {
      addToCartButtons = await page.$$('button:has-text("Add to Cart")');
    }
    if (addToCartButtons.length === 0) {
      addToCartButtons = await page.$$('//button[contains(text(),"Add to Cart")]');
    }

    // If still nothing, throw error
    if (addToCartButtons.length === 0) {
      throw new Error('No add-to-cart buttons found on the page.');
    }

    // Rapidly add and remove items from cart
    // We'll use the first 3 add-to-cart buttons for the stress test
    const numCycles = 8;
    const numItems = Math.min(3, addToCartButtons.length);
    const cartCountSelector = '#cartCount, .cart-count, span:has-text("Cart")';

    for (let cycle = 0; cycle < numCycles; cycle++) {
      // Add items to cart quickly
      for (let i = 0; i < numItems; i++) {
        await addToCartButtons[i].click();
        // Wait for cart count to update
        await page.waitForTimeout(100); // short delay to simulate rapid user
      }
      // Wait for cart count to reflect additions
      await page.waitForTimeout(300);

      // Take screenshot after adding
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_cycle${cycle + 1}_added.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_cycle${cycle + 1}_added.png`));

      // Try to find remove buttons (try multiple selectors)
      let removeButtons = await page.$$('.remove-item');
      if (removeButtons.length === 0) {
        removeButtons = await page.$$('button:has-text("Remove")');
      }
      if (removeButtons.length === 0) {
        removeButtons = await page.$$('//button[contains(text(),"Remove")]');
      }

      // Remove items rapidly if present
      if (removeButtons.length > 0) {
        for (let i = 0; i < Math.min(numItems, removeButtons.length); i++) {
          await removeButtons[i].click();
          await page.waitForTimeout(100);
        }
        await page.waitForTimeout(300);
      } else {
        // If no remove buttons, try to open cart and look for them
        const cartIcon = await page.$('.cart, .fa-shopping-cart, span.cart-text, [id="cartCount"]');
        if (cartIcon) {
          await cartIcon.click();
          await page.waitForTimeout(500);
          removeButtons = await page.$$('.remove-item');
          if (removeButtons.length === 0) {
            removeButtons = await page.$$('button:has-text("Remove")');
          }
          if (removeButtons.length === 0) {
            removeButtons = await page.$$('//button[contains(text(),"Remove")]');
          }
          for (let i = 0; i < Math.min(numItems, removeButtons.length); i++) {
            await removeButtons[i].click();
            await page.waitForTimeout(100);
          }
          await page.waitForTimeout(300);
        }
      }

      // Take screenshot after removing
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_cycle${cycle + 1}_removed.png`) });
      screenshots.push(path.join(screenshotsDir, `step_3_cycle${cycle + 1}_removed.png`));
    }

    // Final cart count extraction
    let cartCount = null;
    try {
      await page.waitForSelector(cartCountSelector, { timeout: 2000 });
      const cartCountElem = await page.$(cartCountSelector);
      if (cartCountElem) {
        cartCount = await cartCountElem.textContent();
        cartCount = cartCount ? cartCount.trim() : null;
      }
    } catch (e) {
      cartCount = null;
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Rapidly added and removed items from cart in quick succession to stress test cart stability. Cart count extracted.",
      extractedData: {
        finalCartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for main content to be present
    await page.waitForSelector('.header', { timeout: 5000 });

    // Scroll to product section - attempt to reveal products dynamically
    // Try common containers for products
    let productSelector = null;
    // Try multiple strategies
    const possibleProductSelectors = [
      'div.product-card',
      'div[class*="product"]',
      'div:has(button:has-text("Add to Cart"))',
      '//button[contains(text(), "Add to Cart")]/ancestor::div[contains(@class, "product")]',
      'img[alt*="iPhone"], img[alt*="Samsung"], img[alt*="Sony"]'
    ];
    for (const sel of possibleProductSelectors) {
      try {
        if (sel.startsWith('//')) {
          const el = await page.$(sel);
          if (el) {
            productSelector = sel;
            break;
          }
        } else {
          const el = await page.$(sel);
          if (el) {
            productSelector = sel;
            break;
          }
        }
      } catch (e) { /* ignore */ }
    }

    // Fallback: click on navigation bar to go to 'Electronics' category
    if (!productSelector) {
      await page.click('text=Electronics');
      await page.waitForTimeout(500);
      await page.waitForSelector('img[alt*="iPhone"]', { timeout: 4000 });
      productSelector = 'img[alt*="iPhone"]';
    }

    // Find all "Add to Cart" buttons visible on the page, using multiple selector strategies
    let addToCartButtons = await page.$$('button:has-text("Add to Cart")');
    if (addToCartButtons.length === 0) {
      // Try alternative: by class
      addToCartButtons = await page.$$('.add-to-cart');
    }
    if (addToCartButtons.length === 0) {
      // Try by xpath (text, fallback)
      addToCartButtons = await page.$$('//button[contains(text(), "Add to Cart")]');
    }

    // Stress test: Rapidly add and remove items from the cart
    let cartCountSelector = '#cartCount, .cart-count, span:has-text("Cart")';
    let cartCountEl = await page.$('#cartCount');
    if (!cartCountEl) {
      cartCountEl = await page.$('.cart-count');
    }
    if (!cartCountEl) {
      const allSpans = await page.$$('span');
      for (const s of allSpans) {
        const text = await s.textContent();
        if (text && text.trim().match(/^\d+$/)) {
          cartCountEl = s;
          break;
        }
      }
    }

    // Use at least 2 products for the stress test (add/remove)
    const buttonsToTest = addToCartButtons.slice(0, 2);

    let addRemoveCycles = 6;
    let addedCounts = [];
    let removedCounts = [];

    for (let i = 0; i < addRemoveCycles; i++) {
      for (let btn of buttonsToTest) {
        // Scroll into view
        await btn.scrollIntoViewIfNeeded();
        // Add to cart
        await btn.click({ delay: 30 });
        // Wait for cart count to update
        await page.waitForTimeout(120);
      }

      // Screenshot after add
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_add_cycle_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_add_cycle_${i + 1}.png`));

      // Read cart count after add
      let countText = 'unknown';
      if (cartCountEl) {
        countText = (await cartCountEl.textContent() || '').trim();
        addedCounts.push(countText);
      }

      // Find remove buttons (try several selector strategies)
      let removeBtns = await page.$$('button:has-text("Remove")');
      if (removeBtns.length === 0) {
        removeBtns = await page.$$('.remove-item');
      }
      if (removeBtns.length === 0) {
        removeBtns = await page.$$('//button[contains(text(), "Remove")]');
      }

      // Remove all items added this cycle (limit to 2 per cycle)
      for (let r = 0; r < buttonsToTest.length && r < removeBtns.length; r++) {
        await removeBtns[r].scrollIntoViewIfNeeded();
        await removeBtns[r].click({ delay: 30 });
        await page.waitForTimeout(120);
      }

      // Screenshot after remove
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_remove_cycle_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_3_remove_cycle_${i + 1}.png`));

      // Read cart count after remove
      if (cartCountEl) {
        const countTextR = (await cartCountEl.textContent() || '').trim();
        removedCounts.push(countTextR);
      }
    }

    // Final screenshot of the cart and cart count
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_cart.png'));

    // Extract final cart count
    let finalCartCount = null;
    if (cartCountEl) {
      finalCartCount = (await cartCountEl.textContent() || '').trim();
    }

    return {
      success: true,
      action: "Rapidly added and removed items from cart in quick succession using multiple selector strategies. Monitored cart count for each cycle.",
      extractedData: {
        addCycles: addRemoveCycles,
        addedCartCounts: addedCounts,
        removedCartCounts: removedCounts,
        finalCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];

    // Initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for at least one product card to appear (try a generic card selector)
    await page.waitForSelector('.product-card, .product, .card, img[alt*="iPhone"], img[alt*="Samsung"]', { timeout: 5000 });

    // Find all visible 'Add to Cart' buttons using multiple strategies
    // Try text content, role, and fuzzy selectors
    const addToCartButtons = await page.$$('//button[contains(translate(.,"ABCDEFGHIJKLMNOPQRSTUVWXYZ","abcdefghijklmnopqrstuvwxyz"),"add to cart")] | //button[contains(@class, "add-to-cart")] | //button[contains(.,"Add to Cart")]');

    if (addToCartButtons.length < 1) {
      throw new Error('No "Add to Cart" buttons found!');
    }

    // We will stress-test by rapidly adding/removing items
    // Pick the first two unique products (if available)
    const buttonsToUse = addToCartButtons.slice(0, 2);

    // Helper: get cart count as integer
    async function getCartCount() {
      // Try several selectors for cart count
      let cartCount = await page.$eval('#cartCount', el => el.textContent.trim());
      if (!cartCount || isNaN(cartCount)) {
        // Try class fallback
        cartCount = await page.$eval('.cart-count', el => el.textContent.trim());
      }
      return parseInt(cartCount, 10);
    }

    // Helper: wait for cart count to be a specific value
    async function waitForCartCount(target, timeout=2000) {
      const start = Date.now();
      while ((Date.now() - start) < timeout) {
        let count = await getCartCount();
        if (count === target) return true;
        await page.waitForTimeout(80);
      }
      return false;
    }

    // Rapidly add/remove items in succession
    for (let i = 0; i < 5; i++) {
      // Add first product
      await buttonsToUse[0].click();
      await page.waitForTimeout(120); // minimal wait for DOM update
      // Add second product if exists
      if (buttonsToUse[1]) {
        await buttonsToUse[1].click();
        await page.waitForTimeout(120);
      }
      // Take screenshot after adds
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_add_iter${i+1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_add_iter${i+1}.png`));

      // Wait for cart count to increase
      let expectedCount = await getCartCount();

      // Now try to remove items rapidly
      // Open cart by clicking on the cart icon (try icon and text fallback)
      const cartIcon = await page.$('.cart, .fa-shopping-cart, [onclick*="showCart"]');
      if (cartIcon) {
        await cartIcon.click();
        // Wait for cart modal/section
        await page.waitForTimeout(350);
        // Try various selectors for "Remove" buttons (case-insensitive)
        const removeBtns = await page.$$('//button[contains(translate(.,"ABCDEFGHIJKLMNOPQRSTUVWXYZ","abcdefghijklmnopqrstuvwxyz"),"remove")] | //button[contains(@class, "remove-item")] | //button[contains(.,"Remove")]');
        // Remove all visible items (or up to 2)
        for (let j = 0; j < Math.min(removeBtns.length, 2); j++) {
          await removeBtns[j].click();
          await page.waitForTimeout(120);
        }
        // Take screenshot after removes
        await page.screenshot({ path: path.join(screenshotsDir, `step_3_remove_iter${i+1}.png`) });
        screenshots.push(path.join(screenshotsDir, `step_3_remove_iter${i+1}.png`));
        // Try to close the cart modal if a close button exists
        const closeBtn = await page.$('button[aria-label="Close"], button.close, button[title="Close"], button:has-text("Ã—")');
        if (closeBtn) {
          await closeBtn.click();
          await page.waitForTimeout(150);
        }
      }
      // Wait for cart count to decrease or reset
      await page.waitForTimeout(200);
    }

    // Final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    // Extract final cart count
    const finalCartCount = await getCartCount();

    return {
      success: true,
      action: "Rapidly added and removed items from the cart in quick succession to stress-test cart stability. Used multiple selectors and strategies for interaction.",
      extractedData: {
        finalCartCount,
        addToCartButtonsDetected: addToCartButtons.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
