import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product grid to appear (assume .product-card or similar)
    // Try to find at least one product with an add-to-cart button
    let productSelector = '.product-card';
    let addToCartSelector = '.add-to-cart';
    let removeItemSelector = '.remove-item';
    let cartCountSelector = '#cartCount';

    // Fallbacks if selectors are not present
    // Try to find a product card by image or title if .product-card is not present
    let foundProduct = false;
    for (let i = 0; i < 10; i++) {
      if (await page.$(productSelector)) {
        foundProduct = true;
        break;
      }
      await page.waitForTimeout(500);
    }
    if (!foundProduct) {
      // Try to find any add-to-cart button directly
      if (!(await page.$(addToCartSelector))) {
        throw new Error('No product card or add-to-cart button found on page');
      }
    }

    // Find all visible add-to-cart buttons (limit to 3 for rapid stress test)
    let addToCartButtons = await page.$$(addToCartSelector);
    if (addToCartButtons.length === 0) {
      throw new Error('No add-to-cart buttons found');
    }
    // Limit to 3 products for rapid stress test
    addToCartButtons = addToCartButtons.slice(0, 3);

    // Rapidly add and remove items from cart in quick succession
    // We'll do 5 cycles of add/remove for each product
    let addRemoveCycles = 5;
    for (let cycle = 0; cycle < addRemoveCycles; cycle++) {
      for (let idx = 0; idx < addToCartButtons.length; idx++) {
        // Add to cart
        await addToCartButtons[idx].click();
        // Wait for cart count to update
        await page.waitForTimeout(100);
      }
      // Wait a bit for UI to settle
      await page.waitForTimeout(300);

      // Try to find all remove-item buttons (assuming they appear after adding)
      let removeButtons = await page.$$(removeItemSelector);
      if (removeButtons.length === 0) {
        // Try to open cart if needed (simulate clicking cart icon)
        const cartIcon = await page.$('.cart, .fa-shopping-cart, text=Cart');
        if (cartIcon) {
          await cartIcon.click();
          await page.waitForTimeout(300);
          removeButtons = await page.$$(removeItemSelector);
        }
      }
      // Remove items (remove as many as we added)
      for (let i = 0; i < addToCartButtons.length && i < removeButtons.length; i++) {
        await removeButtons[i].click();
        await page.waitForTimeout(100);
      }
      // Wait a bit for UI to settle
      await page.waitForTimeout(300);
    }

    // After stress test, take screenshot of cart count and cart UI
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_stress_test.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_stress_test.png'));

    // Extract cart count
    await page.waitForSelector(cartCountSelector, { timeout: 2000 });
    const cartCountText = await page.$eval(cartCountSelector, el => el.textContent.trim());
    // Also extract if any item is still in cart (look for .remove-item)
    const remainingRemoveButtons = await page.$$(removeItemSelector);
    const remainingItems = remainingRemoveButtons.length;

    // Take screenshot of cart if possible
    const cartIcon = await page.$('.cart, .fa-shopping-cart, text=Cart');
    if (cartIcon) {
      await cartIcon.click();
      await page.waitForTimeout(400);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_open.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_cart_open.png'));
    }

    return {
      success: true,
      action: "Rapidly added and removed items from cart in quick succession to stress test cart manipulation and observed cart count and UI.",
      extractedData: {
        cartCount: cartCountText,
        remainingCartItems: remainingItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Screenshot 1: initial state
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for at least one product "Add to Cart" button to appear using a robust selector
    // We'll look for a button with .add-to-cart class inside a product card or similar
    // Since HTML is truncated, fallback to a button with text "Add to Cart" or similar

    // Strategy: Try different selectors for add-to-cart
    let addToCartButtons = await page.$$('button.add-to-cart');
    if (addToCartButtons.length === 0) {
      addToCartButtons = await page.$$('//button[contains(text(), "Add to Cart")]');
    }
    if (addToCartButtons.length === 0) {
      addToCartButtons = await page.$$('//button[contains(., "Add to Cart")]');
    }
    if (addToCartButtons.length === 0) {
      // Try a more generic selector: any button inside a product card
      addToCartButtons = await page.$$('div.product-card button');
    }
    if (addToCartButtons.length === 0) {
      throw new Error("No add-to-cart buttons found");
    }

    // Wait for cart count element by ID and by class as fallback
    let cartCountSelector = '#cartCount';
    let cartCountElem = await page.$(cartCountSelector);
    if (!cartCountElem) {
      cartCountSelector = '.cart-count';
      cartCountElem = await page.$(cartCountSelector);
    }
    if (!cartCountElem) {
      throw new Error('Cart count element not found');
    }

    // Rapidly add and remove items from cart in quick succession
    // We'll stress test by clicking Add to Cart and Remove (if available) in a loop

    const iterations = 8; // Number of rapid add/remove cycles

    // Record cart count before test
    let cartCountBefore = await page.$eval(cartCountSelector, el => el.innerText);

    // Try to find remove buttons for the cart after adding
    // We'll assume .remove-item class for remove buttons

    for (let i = 0; i < iterations; i++) {
      // Add to Cart (try different buttons in sequence to stress test)
      const buttonIdx = i % addToCartButtons.length;
      await addToCartButtons[buttonIdx].click();
      // Wait for cart count to increment (or briefly wait if not instant)
      await page.waitForTimeout(120);

      // Try to open the cart view/panel if needed so "Remove" buttons are available
      // Click the cart icon (by class or text)
      if (i === 0) {
        // Only do this first time to avoid excessive toggling
        let cartIcon = await page.$('div.cart');
        if (!cartIcon) {
          // Try by text
          cartIcon = await page.$('//span[contains(text(),"Cart")]');
        }
        if (cartIcon) {
          await cartIcon.click();
          // Wait for cart UI to update
          await page.waitForTimeout(250);
        }
      }

      // Wait a moment for cart change to render
      await page.waitForTimeout(100);

      // Look for remove button(s) inside the cart
      let removeButtons = await page.$$('.remove-item');
      if (removeButtons.length === 0) {
        // Try by text content
        removeButtons = await page.$$('//button[contains(text(),"Remove")]');
      }
      if (removeButtons.length > 0) {
        // Remove the item we just added (simulate rapid add/remove)
        // We'll always click the first one for rapidity
        await removeButtons[0].click();
        await page.waitForTimeout(120);
      }
      // Otherwise, just continue with next add/remove (no error)
    }

    // Screenshot 2: after stress test
    const step2 = path.join(screenshotsDir, 'step_2_after_stress.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Extract cart count after test
    await page.waitForTimeout(200); // Wait for UI to stabilize
    let cartCountAfter = await page.$eval(cartCountSelector, el => el.innerText);

    // Gather data for verification
    const extractedData = {
      cartCountBefore,
      cartCountAfter,
      addToCartButtonsUsed: addToCartButtons.length,
      testIterations: iterations
    };

    return {
      success: true,
      action: `Rapidly added and removed items from cart (${iterations} cycles) using multiple selectors to stress test cart manipulation. Cart count before: ${cartCountBefore}, after: ${cartCountAfter}`,
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for featured product section to load (fallback: wait for main content)
    await page.waitForSelector('main, #mainContent, .product-listing, .product-card', { timeout: 4000 });

    // Find all visible product cards
    let productCards = await page.$$('.product-card');
    if (productCards.length === 0) {
      // Try alternative: cards in a grid
      productCards = await page.$$('.product-tile, .product-listing .product');
    }
    if (productCards.length === 0) {
      throw new Error('No product cards found on the page');
    }

    // Use the first product card for stress test
    const card = productCards[0];

    // Try to locate "Add to Cart" button inside card: look for text, icon, or button
    let addSelector = [
      '.add-to-cart',
      'button:has-text("Add to Cart")',
      'button[title*="Add"]',
      'button[aria-label*="add"]',
      'button:has(i.fa-cart-plus)',
      'button:has-text("Add")'
    ];

    let addBtn = null;
    for (const sel of addSelector) {
      addBtn = await card.$(sel);
      if (addBtn) break;
    }
    if (!addBtn) {
      // As fallback, try to find any "Add to Cart" on the page
      for (const sel of addSelector) {
        addBtn = await page.$(sel);
        if (addBtn) break;
      }
    }
    if (!addBtn) throw new Error('Could not find an "Add to Cart" button');

    // Find cart counter element (try both #cartCount and .cart-count)
    let cartCountEl = await page.$('#cartCount');
    if (!cartCountEl) cartCountEl = await page.$('.cart-count');
    if (!cartCountEl) throw new Error('Cart count element not found');

    // Rapidly add and remove item from the cart
    const iterations = 10;
    let cartCounts = [];
    for (let i = 0; i < iterations; i++) {
      // Add to cart
      await addBtn.click();
      await page.waitForTimeout(100);

      // Wait for cartCount to increment (max 1s)
      await page.waitForFunction(
        (el, prev) => parseInt(el.textContent) > prev,
        cartCountEl,
        cartCounts.length > 0 ? cartCounts[cartCounts.length - 1] : 0,
        { timeout: 1000 }
      ).catch(() => {}); // Don't fail if not incremented

      // Record cart count
      let countText = await cartCountEl.textContent();
      cartCounts.push(parseInt(countText));

      // Now, open cart interface if needed (try clicking cart icon)
      let cartIcon = await page.$('.cart, .fa-shopping-cart, [aria-label*="cart"]');
      if (cartIcon) {
        await cartIcon.click();
        await page.waitForTimeout(150);
      }

      // Try to find a remove button for the added item
      let removeBtn = null;
      // Try various selectors for remove
      const removeSelectors = [
        '.remove-item',
        'button:has-text("Remove")',
        'button[title*="Remove"]',
        'button[aria-label*="remove"]',
        'button:has(i.fa-trash)',
        'button:has-text("Delete")'
      ];
      for (const sel of removeSelectors) {
        removeBtn = await page.$(sel);
        if (removeBtn) break;
      }
      if (!removeBtn) {
        // As fallback, try inside cart popup/modal
        const cartModal = await page.$('.cart-modal, .cart-popup, .cart-items');
        if (cartModal) {
          for (const sel of removeSelectors) {
            removeBtn = await cartModal.$(sel);
            if (removeBtn) break;
          }
        }
      }

      if (removeBtn) {
        await removeBtn.click();
        await page.waitForTimeout(100);

        // Wait for cartCount to decrement (max 1s)
        await page.waitForFunction(
          (el, prev) => parseInt(el.textContent) < prev,
          cartCountEl,
          cartCounts[cartCounts.length - 1],
          { timeout: 1000 }
        ).catch(() => {});
        // Record cart count after removal
        let afterRemoveCount = await cartCountEl.textContent();
        cartCounts.push(parseInt(afterRemoveCount));
      }
      // Optional: close cart popup/modal if present
      const closeCartBtn = await page.$('button.close-cart, .cart-modal button:has-text("Ã—"), .cart-modal .close');
      if (closeCartBtn) {
        await closeCartBtn.click();
        await page.waitForTimeout(80);
      }

      // Take a screenshot after each add/remove cycle (every 2 cycles)
      if (i % 2 === 1) {
        const fname = `step_${2 + Math.floor(i/2)}_cycle_${i+1}.png`;
        await page.screenshot({ path: path.join(screenshotsDir, fname) });
        screenshots.push(path.join(screenshotsDir, fname));
      }
    }

    // Final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_final.png'));

    // Extract final cart count
    let finalCartCount = await cartCountEl.textContent();

    return {
      success: true,
      action: "Rapidly added and removed items from the cart in quick succession to stress-test cart logic and UI updates.",
      extractedData: {
        cartCountsSequence: cartCounts,
        finalCartCount: finalCartCount.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
