import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput');

    // Input JavaScript code to test for XSS vulnerability
    await page.fill('#searchInput', '<script>alert("XSS")</script>');

    // Take a screenshot after inputting the script
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_input_script.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_input_script.png'));

    // Click the search button to perform the search
    await page.click('.search-btn');

    // Wait for the search results to load
    await page.waitForSelector('.search-results');

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract meaningful data to verify task completion
    const alertTriggered = await page.evaluate(() => {
      return !!document.querySelector('script') && document.querySelector('script').innerHTML.includes('alert("XSS")');
    });

    return {
      success: true,
      action: "Attempted XSS via search input",
      extractedData: { alertTriggered },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

