import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible and interactable
    await page.waitForSelector('#searchInput', { state: 'visible' });

    // Type the XSS payload into the search input field
    await page.fill('#searchInput', '<script>alert("XSS")</script>');

    // Take a screenshot after entering the payload
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_payload_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_payload_entered.png'));

    // Click the search button to trigger the search
    await page.click('.search-btn');

    // Wait for the search results to load
    await page.waitForSelector('.search-results', { state: 'visible' });

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract meaningful data to verify task completion
    const resultsText = await page.textContent('.search-results');

    return {
      success: true,
      action: "Entered XSS payload into search input and triggered search",
      extractedData: { resultsText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
