import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and enter XSS payload
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', '<script>alert("XSS")</script>');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_xss_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_xss_input.png'));

    // Click search button
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Wait for search results and take screenshot
    await page.waitForSelector('.search-results', { timeout: 5000 }).catch(() => {});
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Check if alert was triggered (XSS successful)
    const alertTriggered = await page.evaluate(() => {
      return typeof window.xssSuccess !== 'undefined' && window.xssSuccess === true;
    });

    return {
      success: true,
      action: "Attempted XSS injection via search input with payload: <script>alert('XSS')</script>",
      extractedData: {
        xssSuccessful: alertTriggered,
        searchTermUsed: '<script>alert("XSS")</script>'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete XSS injection attempt",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

