    screenshots.push(path.join(screenshotsDir, 'step_2_xss_input.png'));

    // Click search button using multiple selector strategies
    const searchBtn = await page.waitForSelector('button.search-btn, button[class*="search"]', { timeout: 3000 });
    await searchBtn.click();

    // Wait for results or potential alert
    await page.waitForTimeout(2000);

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Check for XSS success by examining page content
    const pageContent = await page.content();
    const xssDetected = pageContent.includes('alert("XSS")') || 
                        pageContent.includes('<script>') ||
                        await page.$('script:contains("XSS")') !== null;

    return {
      success: true,
      action: "Attempted XSS injection via search input with payload: <script>alert('XSS')</script>",
      extractedData: {
        xssPayloadUsed: '<script>alert("XSS")</script>',
        xssVulnerabilityDetected: xssDetected,
        searchPerformed: true,
        pageTitle: await page.title()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
