import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and button to be available
    await page.waitForSelector('#searchInput');
    await page.waitForSelector('button.search-btn');

    // Input XSS test script into search field
    const xssPayload = '<script>alert("XSS")</script>';
    await page.fill('#searchInput', xssPayload);

    // Take screenshot after filling search input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_filled.png'));

    // Click search button
    await page.click('button.search-btn');

    // Wait for potential results or page changes
    await page.waitForTimeout(2000);

    // Take screenshot after search submission
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Check for alert dialog (XSS success indicator)
    let alertTriggered = false;
    page.on('dialog', async dialog => {
      alertTriggered = true;
      await dialog.dismiss();
    });

    // Check if search results contain XSS payload
    const pageContent = await page.content();
    const xssDetected = pageContent.includes(xssPayload);

    return {
      success: true,
      action: "Attempted XSS injection via search input with payload: " + xssPayload,
      extractedData: {
        alertTriggered: alertTriggered,
        payloadReflected: xssDetected,
        pageContentSnippet: pageContent.substring(0, 500) + '...'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete XSS injection test",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

