import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and enter XSS payload
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', '<script>alert("XSS")</script>');

    // Take screenshot after input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_xss_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_xss_input.png'));

    // Click search button using multiple selector strategies
    await Promise.race([
      page.click('button.search-btn'),
      page.click('button:has-text("Search")'),
      page.click('button >> text=Search')
    ]);

    // Wait for results and take screenshot
    await page.waitForTimeout(2000); // Wait for potential alert/response
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Check for alert dialog (XSS might trigger it)
    let alertTriggered = false;
    page.on('dialog', async dialog => {
      alertTriggered = true;
      await dialog.dismiss();
    });

    // Extract search results content
    const searchResults = await page.$eval('.search-results', el => el.textContent.trim())
      .catch(() => 'No search results container found');

    return {
      success: true,
      action: "Attempted XSS injection via search input with payload: <script>alert('XSS')</script>",
      extractedData: {
        alertTriggered,
        searchResults,
        inputValue: await page.$eval('#searchInput', el => el.value)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete XSS injection attempt",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

