import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract cart contents
    const cartContents = await page.evaluate(() => {
      const cartItems = [];
      const items = document.querySelectorAll('.cart-item');
      items.forEach(item => {
        cartItems.push({
          title: item.querySelector('.product-title').innerText,
          price: item.querySelector('.product-price').innerText
        });
      });
      return cartItems;
    });

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract product information
    const products = await page.evaluate(() => {
      const productList = [];
      const productCards = document.querySelectorAll('.product-card');
      productCards.forEach(card => {
        productList.push({
          title: card.querySelector('.product-title').innerText,
          price: card.querySelector('.product-price').innerText,
          rating: card.querySelector('.product-rating').innerText,
          image: card.querySelector('.product-image').src
        });
      });
      return productList;
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted localStorage, cart contents, search history, and product information",
      extractedData: {
        localStorage: localStorageData,
        cart: cartContents,
        searchHistory: searchHistory,
        products: products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

