import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and capture its value
    await page.waitForSelector('#searchInput');
    const searchInputValue = await page.$eval('#searchInput', el => el.value);

    // Wait for cart count and capture its value
    await page.waitForSelector('#cartCount');
    const cartCount = await page.$eval('#cartCount', el => el.innerText);

    // Wait for product cards and extract product information
    await page.waitForSelector('.product-card');
    const products = await page.$$eval('.product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title').innerText,
        price: card.querySelector('.product-price').innerText,
        rating: card.querySelector('.product-rating').innerText,
      }));
    });

    // Capture localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Take a screenshot of the cart
    await page.click('.cart');
    await page.waitForSelector('.cart-item');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    // Extract cart items
    const cartItems = await page.$$eval('.cart-item', items => {
      return items.map(item => ({
        title: item.querySelector('.item-title').innerText,
        quantity: item.querySelector('.item-quantity').innerText,
        price: item.querySelector('.item-price').innerText,
      }));
    });

    return {
      success: true,
      action: "Extracted data from localStorage, cart, and product information",
      extractedData: {
        searchInputValue,
        cartCount,
        products,
        localStorageData,
        cartItems,
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

