import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract cart contents
    const cartContents = await page.evaluate(() => {
      const cartItems = [];
      const cartElements = document.querySelectorAll('.cart-item');
      cartElements.forEach(item => {
        const title = item.querySelector('.product-title')?.innerText || '';
        const price = item.querySelector('.product-price')?.innerText || '';
        cartItems.push({ title, price });
      });
      return cartItems;
    });

    // Extract search history (mocked as an example)
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract product information
    const products = await page.evaluate(() => {
      const productList = [];
      const productElements = document.querySelectorAll('.product-card');
      productElements.forEach(product => {
        const title = product.querySelector('.product-title')?.innerText || '';
        const price = product.querySelector('.product-price')?.innerText || '';
        const rating = product.querySelector('.product-rating')?.innerText || '';
        productList.push({ title, price, rating });
      });
      return productList;
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, cart contents, search history, and product information.",
      extractedData: {
        localStorage: JSON.parse(localStorageData),
        cartContents,
        searchHistory,
        products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

