import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => {
      return items.map(item => ({
        title: item.querySelector('.product-title') ? item.querySelector('.product-title').innerText : null,
        price: item.querySelector('.product-price') ? item.querySelector('.product-price').innerText : null,
      }));
    });

    // Extract search history from the page (if available)
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extraction.png'));

    // Return extracted data
    return {
      success: true,
      action: "Extracted localStorage, cart contents, and search history",
      extractedData: {
        localStorage: localStorageData,
        cartItems,
        searchHistory
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

