import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => {
      return items.map(item => ({
        title: item.querySelector('.item-title')?.textContent || '',
        price: item.querySelector('.item-price')?.textContent || '',
        quantity: item.querySelector('.item-quantity')?.textContent || '',
      }));
    });

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract product information
    const productCards = await page.$$eval('.product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent || '',
        price: card.querySelector('.product-price')?.textContent || '',
        rating: card.querySelector('.product-rating')?.textContent || '',
        image: card.querySelector('.product-image')?.src || '',
      }));
    });

    // Take screenshot of cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage data, cart contents, search history, and product information.",
      extractedData: {
        localStorage: localStorageData,
        cart: cartItems,
        searchHistory: searchHistory,
        products: productCards,
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
