import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => {
      return items.map(item => ({
        title: item.querySelector('.item-title')?.innerText,
        price: item.querySelector('.item-price')?.innerText,
      }));
    });

    // Extract search history (mocked as an example)
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });

    // Extract product information
    const products = await page.$$eval('.product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title')?.innerText,
        price: card.querySelector('.product-price')?.innerText,
        rating: card.querySelector('.product-rating')?.innerText,
      }));
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, cart contents, search history, and product information.",
      extractedData: {
        localStorage: localStorageData,
        cart: cartItems,
        searchHistory: searchHistory,
        products: products,
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

