import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });
    console.log('LocalStorage Data:', localStorageData);

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => {
      return items.map(item => ({
        title: item.querySelector('.product-title').innerText,
        price: item.querySelector('.product-price').innerText,
      }));
    });
    console.log('Cart Items:', cartItems);

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory')) || [];
    });
    console.log('Search History:', searchHistory);

    // Extract product information
    const products = await page.$$eval('.product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title').innerText,
        price: card.querySelector('.product-price').innerText,
        rating: card.querySelector('.product-rating').innerText,
        reviews: card.querySelector('.product-reviews').innerText,
      }));
    });
    console.log('Products:', products);

    // Take final screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Extracted localStorage, cart contents, search history, and product information.",
      extractedData: {
        localStorage: localStorageData,
        cartItems,
        searchHistory,
        products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

