import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Collect localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Collect cart contents
    const cartContents = await page.evaluate(() => {
      const cartItems = [];
      const items = document.querySelectorAll('.cart-item');
      items.forEach(item => {
        const title = item.querySelector('.product-title') ? item.querySelector('.product-title').innerText : null;
        const quantity = item.querySelector('.item-quantity') ? item.querySelector('.item-quantity').innerText : null;
        cartItems.push({ title, quantity });
      });
      return cartItems;
    });

    // Collect product information
    const productInformation = await page.evaluate(() => {
      const products = [];
      const productCards = document.querySelectorAll('.product-card');
      productCards.forEach(card => {
        const title = card.querySelector('.product-title') ? card.querySelector('.product-title').innerText : null;
        const price = card.querySelector('.product-price') ? card.querySelector('.product-price').innerText : null;
        const rating = card.querySelector('.product-rating') ? card.querySelector('.product-rating').innerText : null;
        products.push({ title, price, rating });
      });
      return products;
    });

    // Take screenshot of cart after collecting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_contents.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_contents.png'));

    return {
      success: true,
      action: "Collected localStorage, cart contents, and product information",
      extractedData: {
        localStorage: JSON.parse(localStorageData),
        cartContents,
        productInformation
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
