    // Extract cart contents
    const cartItems = await page.evaluate(() => {
      const items = [];
      const cartElements = document.querySelectorAll('.cart-item');
      cartElements.forEach(item => {
        const title = item.querySelector('.product-title') ? item.querySelector('.product-title').innerText : '';
        const price = item.querySelector('.product-price') ? item.querySelector('.product-price').innerText : '';
        items.push({ title, price });
      });
      return items;
