        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract product information
    const productData = await page.evaluate(() => {
      const products = [];
      const productCards = document.querySelectorAll('.product-card');

      productCards.forEach(card => {
        products.push({
          title: card.querySelector('.product-title')?.textContent?.trim(),
          price: card.querySelector('.product-price')?.textContent?.trim(),
          rating: card.querySelector('.product-rating')?.textContent?.trim(),
          image: card.querySelector('.product-image')?.src
        });
      });

      return products;
    });

    // Extract cart contents
    await page.click('.cart');
    await page.waitForTimeout(1000);

    const cartData = await page.evaluate(() => {
      const cartItems = [];
      const items = document.querySelectorAll('.cart-item');

      items.forEach(item => {
        cartItems.push({
          name: item.querySelector('.cart-item-name')?.textContent?.trim(),
          price: item.querySelector('.cart-item-price')?.textContent?.trim(),
          quantity: item.querySelector('.cart-item-quantity')?.textContent?.trim()
        });
      });

      return cartItems;
    });

    // Extract search history (if available)
    await page.click('#searchInput');
    await page.waitForTimeout(500);

    const searchHistory = await page.evaluate(() => {
      const suggestions = document.querySelectorAll('.suggestion-item');
      return Array.from(suggestions).map(s => s.textContent.trim());
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, product information, cart contents, and search history",
      extractedData: {
        localStorage: localStorageData,
        products: productData,
        cart: cartData,
        searchHistory: searchHistory
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete data extraction task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

