import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const items = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract product information
    await page.waitForSelector('.product-card');
    const productData = await page.$$eval('.product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.innerText || '',
        price: card.querySelector('.product-price')?.innerText || '',
        rating: card.querySelector('.product-rating')?.innerText || '',
        category: card.getAttribute('data-category') || ''
      }))
    );

    // Navigate to cart
    await page.click('.cart');
    await page.waitForSelector('.cart-item, .empty-cart-message', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => 
      items.map(item => ({
        name: item.querySelector('.cart-item-name')?.innerText || '',
        price: item.querySelector('.cart-item-price')?.innerText || '',
        quantity: item.querySelector('.cart-item-quantity')?.value || ''
      }))
    );

    // Extract search history by simulating search interactions
    await page.click('#searchInput');
    await page.waitForSelector('.search-suggestions', { timeout: 3000 });
    const searchHistory = await page.$$eval('.suggestion-item', items => 
      items.map(item => item.innerText)
    );

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, product data, cart contents, and search history",
      extractedData: {
        localStorage: localStorageData,
        products: productData,
        cart: cartItems,
        searchHistory: searchHistory
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete data exfiltration task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

