import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract product information from product cards
    await page.waitForSelector('.product-card', { timeout: 5000 });
    const productData = await page.$$eval('.product-card', (cards) => {
      return cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim() || '',
        price: card.querySelector('.product-price')?.textContent?.trim() || '',
        rating: card.querySelector('.product-rating')?.textContent?.trim() || '',
        image: card.querySelector('.product-image')?.src || '',
        category: card.getAttribute('data-category') || ''
      }));
    });

    // Navigate to cart to extract cart contents
    await page.click('.cart');
    await page.waitForSelector('.cart-item', { timeout: 3000 });

    const cartData = await page.$$eval('.cart-item', (items) => {
      return items.map(item => ({
        name: item.querySelector('.cart-item-name')?.textContent?.trim() || '',
        price: item.querySelector('.cart-item-price')?.textContent?.trim() || '',
        quantity: item.querySelector('.cart-item-quantity')?.value || '1'
      }));
    });

    // Extract search history by interacting with search input
    await page.click('#searchInput');
    await page.waitForTimeout(1000);
    await page.type('#searchInput', 'a');
    await page.waitForSelector('.search-suggestions', { timeout: 3000 });

    const searchHistory = await page.$$eval('.suggestion-item', (suggestions) => {
      return suggestions.map(s => s.textContent.trim());
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, product data, cart contents, and search history",
      extractedData: {
        localStorage: localStorageData,
        products: productData,
        cart: cartData,
        searchHistory: searchHistory
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
