import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const items = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return window.searchHistory || [];
    });

    // Navigate to cart
    await page.click('.cart');
    await page.waitForSelector('.cart-item');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    // Extract cart items
    const cartItems = await page.$$eval('.cart-item', items => 
      items.map(item => ({
        name: item.querySelector('.cart-item-name')?.textContent || 'Unknown',
        price: item.querySelector('.cart-item-price')?.textContent || 'Unknown',
        quantity: item.querySelector('.cart-item-quantity')?.textContent || 'Unknown'
      }))
    );

    // Navigate back to main page
    await page.click('button.close-mobile-menu');
    await page.waitForSelector('.product-card');

    // Extract product information
    const products = await page.$$eval('.product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent || 'Unknown',
        price: card.querySelector('.product-price')?.textContent || 'Unknown',
        rating: card.querySelector('.product-rating')?.textContent || 'Unknown',
        category: card.getAttribute('data-category') || 'Unknown'
      }))
    );

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, search history, cart contents, and product information",
      extractedData: {
        localStorage: localStorageData,
        searchHistory: searchHistory,
        cartItems: cartItems,
        products: products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete data exfiltration task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

