import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Click on cart to view cart contents
    await page.click('div.cart');
    await page.waitForSelector('.cart-item, .empty-cart-message', { timeout: 5000 });

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', items => 
      items.map(item => ({
        title: item.querySelector('.item-title')?.textContent?.trim(),
        price: item.querySelector('.item-price')?.textContent?.trim(),
        quantity: item.querySelector('.item-quantity')?.textContent?.trim()
      }))
    );

    // Extract search history from localStorage and page elements
    const searchHistory = await page.evaluate(() => {
      const history = [];
      // Check if search history exists in localStorage
      const storedHistory = localStorage.getItem('searchHistory');
      if (storedHistory) {
        history.push(...JSON.parse(storedHistory));
      }

      // Also try to get search suggestions if available
      const suggestions = document.querySelectorAll('.suggestion-item');
      suggestions.forEach(suggestion => {
        history.push(suggestion.textContent.trim());
      });

      return [...new Set(history)]; // Remove duplicates
    });

    // Extract product information
    const products = await page.$$eval('.product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim(),
        price: card.querySelector('.product-price')?.textContent?.trim(),
        rating: card.querySelector('.product-rating')?.textContent?.trim(),
        category: card.getAttribute('data-category') || 'unknown'
      }))
    );

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, cart contents, search history, and product information",
      extractedData: {
        localStorage: localStorageData,
        cartItems: cartItems.length > 0 ? cartItems : 'Cart is empty',
        searchHistory,
        products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete data exfiltration task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
