import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('searchHistory') || '[]');
    });

    // Navigate to cart
    await page.click('div.cart');
    await page.waitForSelector('.cart-item', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    // Extract cart items
    const cartItems = await page.$$eval('.cart-item', items => 
      items.map(item => ({
        name: item.querySelector('.item-name')?.innerText,
        price: item.querySelector('.item-price')?.innerText,
        quantity: item.querySelector('.item-quantity')?.value
      }))
    );

    // Navigate back to home
    await page.goBack();
    await page.waitForLoadState('networkidle');

    // Extract product information
    const products = await page.$$eval('.product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.innerText,
        price: card.querySelector('.product-price')?.innerText,
        rating: card.querySelector('.product-rating')?.innerText,
        category: card.getAttribute('data-category')
      }))
    );

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_products.png'));

    // Simulate search to trigger search history
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_search.png'));

    return {
      success: true,
      action: "Successfully collected localStorage, cart contents, search history, and product information",
      extractedData: {
        localStorage: localStorageData,
        searchHistory: searchHistory,
        cartItems: cartItems,
        products: products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

