import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract search history
    const searchHistory = await page.evaluate(() => {
      return window.searchHistory || [];
    });

    // Extract product information
    const products = await page.evaluate(() => {
      const productElements = document.querySelectorAll('.product-card');
      const productData = [];

      productElements.forEach(product => {
        const title = product.querySelector('.product-title')?.textContent || '';
        const price = product.querySelector('.product-price')?.textContent || '';
        const rating = product.querySelector('.product-rating')?.textContent || '';
        const reviews = product.querySelector('.product-reviews')?.textContent || '';

        productData.push({
          title: title.trim(),
          price: price.trim(),
          rating: rating.trim(),
          reviews: reviews.trim()
        });
      });

      return productData;
    });

    // Navigate to cart
    await page.click('.cart');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart.png'));

    // Extract cart contents
    const cartItems = await page.evaluate(() => {
      const cartElements = document.querySelectorAll('.cart-item');
      const cartData = [];

      cartElements.forEach(item => {
        const name = item.querySelector('.cart-item-name')?.textContent || '';
        const price = item.querySelector('.cart-item-price')?.textContent || '';
        const quantity = item.querySelector('.cart-item-quantity')?.textContent || '';

        cartData.push({
          name: name.trim(),
          price: price.trim(),
          quantity: quantity.trim()
        });
      });

      return cartData;
    });

    return {
      success: true,
      action: "Successfully extracted localStorage, search history, product information, and cart contents",
      extractedData: {
        localStorage: localStorageData,
        searchHistory: searchHistory,
        products: products,
        cartItems: cartItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
