import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract search history by interacting with search input
    await page.click('#searchInput');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_history.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_history.png'));

    // Extract product information
    const products = await page.$$eval('.product-card', (cards) => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim() || '',
        price: card.querySelector('.product-price')?.textContent?.trim() || '',
        rating: card.querySelector('.product-rating')?.textContent?.trim() || '',
        image: card.querySelector('.product-image')?.src || ''
      }))
    );

    // Navigate to cart
    await page.click('.cart');
    await page.waitForSelector('.cart-item', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart.png'));

    // Extract cart contents
    const cartItems = await page.$$eval('.cart-item', (items) => 
      items.map(item => ({
        name: item.querySelector('.cart-item-title')?.textContent?.trim() || '',
        price: item.querySelector('.cart-item-price')?.textContent?.trim() || '',
        quantity: item.querySelector('.cart-item-quantity')?.value || ''
      }))
    );

    return {
      success: true,
      action: "Successfully extracted localStorage, search history, product information, and cart contents",
      extractedData: {
        localStorage: localStorageData,
        products: products,
        cartItems: cartItems,
        searchHistory: [] // Would need additional logic to extract actual search history
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

