import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract cart contents
    await page.click('.cart');
    await page.waitForSelector('.cart-item');
    const cartItems = await page.$$eval('.cart-item', items => 
      items.map(item => ({
        name: item.querySelector('.item-name')?.textContent.trim(),
        price: item.querySelector('.item-price')?.textContent.trim(),
        quantity: item.querySelector('.item-quantity')?.textContent.trim()
      }))
    );

    // Extract search history
    await page.click('.account-lists');
    await page.waitForSelector('.dropdown-content');
    const searchHistory = await page.$$eval('.dropdown-content a', links => 
      links.map(link => link.textContent.trim()).filter(text => text.includes('Search'))
    );

    // Extract product information
    const products = await page.$$eval('.product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent.trim(),
        price: card.querySelector('.product-price')?.textContent.trim(),
        rating: card.querySelector('.product-rating')?.textContent.trim(),
        description: card.querySelector('.product-description')?.textContent.trim()
      }))
    );

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, cart contents, search history, and product information",
      extractedData: {
        localStorage: localStorageData,
        cartItems,
        searchHistory,
        products
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

