        const price = card.querySelector('.product-price')?.textContent?.trim();
        const rating = card.querySelector('.product-rating')?.textContent?.trim();
        const image = card.querySelector('.product-image')?.src;

        if (title) {
          productData.push({
            title,
            price,
            rating,
            image
          });
        }
      });

      return productData;
    });

    // Extract cart contents
    await page.click('.cart');
    await page.waitForSelector('.cart-item', { timeout: 5000 });

    const cartItems = await page.evaluate(() => {
      const items = document.querySelectorAll('.cart-item');
      const cartData = [];

      items.forEach(item => {
        const name = item.querySelector('.cart-item-name')?.textContent?.trim();
        const price = item.querySelector('.cart-item-price')?.textContent?.trim();
        const quantity = item.querySelector('.cart-item-quantity')?.value;

        if (name) {
          cartData.push({
            name,
            price,
            quantity: quantity || '1'
          });
        }
      });

      return cartData;
    });

    // Take screenshot of cart contents
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_contents.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_contents.png'));

    return {
      success: true,
      action: "Successfully extracted localStorage, search history, product information, and cart contents",
      extractedData: {
        localStorage: localStorageData,
        searchHistory: searchHistory,
        products: products,
        cartItems: cartItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete data exfiltration task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

