import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar navigation to be visible
    await page.waitForSelector('.sidebar .nav-menu');

    // Find the Search nav item using data-view attribute
    const searchNavSelector = '.nav-item[data-view="search"]';
    await page.waitForSelector(searchNavSelector);

    // Click the Search nav item
    await page.click(searchNavSelector);

    // Wait for the Search View to become active
    await page.waitForSelector('#search-view.view.active', { timeout: 3000 });

    // Wait for the search input to be visible
    await page.waitForSelector('#search-input', { timeout: 2000 });

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Extract some data to verify we're in the search view
    const isSearchViewActive = await page.$eval('#search-view', el => el.classList.contains('active'));
    const searchHeader = await page.$eval('#search-view .content-header .search-container input', el => el.getAttribute('placeholder'));
    const searchTabs = await page.$$eval('#search-view .search-tabs .tab-btn', els => els.map(e => e.textContent.trim()));

    return {
      success: true,
      action: "Navigated to Search View via sidebar navigation and verified search UI is visible.",
      extractedData: {
        isSearchViewActive,
        searchHeader,
        searchTabs
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

