import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for the sidebar navigation to be visible
    await page.waitForSelector('nav.sidebar ul.nav-menu');

    // Find the "Search" nav item using text and class (different selector strategy)
    // The nav items are <li class="nav-item" data-view="search">
    // We'll use XPath to find the <li> with a child <span> containing "Search"
    const [searchNavItem] = await page.$x("//nav[contains(@class, 'sidebar')]//li[contains(@class, 'nav-item') and @data-view='search' and .//span[normalize-space(text())='Search']]");
    if (!searchNavItem) {
      throw new Error('Could not find the Search navigation item');
    }

    // Scroll into view for visibility and click
    await searchNavItem.scrollIntoViewIfNeeded();
    await searchNavItem.click();

    // Wait for the Search View to become active
    // The view is <div id="search-view" class="view"> and should get "active" class
    await page.waitForSelector('#search-view.view.active', { timeout: 3000 });

    // Wait for the search input to be visible and enabled
    await page.waitForSelector('#search-input:enabled', { timeout: 2000 });

    // Take screenshot after navigation
    const searchScreenshot = path.join(screenshotsDir, 'step_2_search_view.png');
    await page.screenshot({ path: searchScreenshot });
    screenshots.push(searchScreenshot);

    // Extract some data to verify we are in the search view
    // For example, get the header text and placeholder of the search input
    const headerText = await page.$eval('#search-view .content-header .search-container input', el => el.placeholder);
    const isSearchTabActive = await page.$eval('#search-view .search-tabs .tab-btn.active', el => el.textContent.trim());
    const searchTabs = await page.$$eval('#search-view .search-tabs .tab-btn', btns => btns.map(b => b.textContent.trim()));

    return {
      success: true,
      action: "Navigated to Search View via sidebar navigation and verified search UI is visible.",
      extractedData: {
        searchInputPlaceholder: headerText,
        activeTab: isSearchTabActive,
        availableTabs: searchTabs
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
