import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread text area to be visible
    await page.waitForSelector('#thread-text');

    // Type a simple text thread
    const threadText = "This is a simple text thread.";
    await page.fill('#thread-text', threadText);

    // Click the post button
    await page.click('#post-thread');

    // Wait for the thread to be posted (you might need to adjust the selector based on the actual implementation)
    await page.waitForSelector('.thread-item');

    // Take a screenshot after posting the thread
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_posting.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_posting.png'));

    // Extract the latest thread content to verify it was posted
    const postedThreadContent = await page.textContent('.thread-item .thread-text');

    return {
      success: true,
      action: "Posted a simple text thread successfully",
      extractedData: { postedThreadContent },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

