import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the "Search" nav item (sidebar)
    // Use both data-view and visible text for robustness
    const searchNavSelector = '.nav-item[data-view="search"]';
    await page.waitForSelector(searchNavSelector, { state: 'visible' });
    await page.click(searchNavSelector);

    // Wait for the search view to be active and input visible
    await page.waitForSelector('#search-view.view.active', { timeout: 3000 });
    await page.waitForSelector('#search-input', { state: 'visible' });

    // Screenshot after switching to search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type a user search query (e.g., "jun")
    const searchQuery = 'jun';
    await page.fill('#search-input', searchQuery);

    // Wait for dynamic search results to appear
    // The results are rendered in #search-content
    // Wait for at least one .search-result-item to appear
    await page.waitForSelector('#search-content .search-result-item', { timeout: 3000 });

    // Screenshot after entering search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract user search results: username, handle, bio
    const userResults = await page.$$eval(
      '#search-content .search-result-item',
      nodes => nodes
        .filter(node => node.querySelector('.search-result-info h4'))
        .map(node => {
          const username = node.querySelector('.search-result-info h4')?.innerText.trim() || '';
          const handleLine = node.querySelector('.search-result-info p')?.innerText.trim() || '';
          const handleMatch = handleLine.match(/(@\w+)/);
          const handle = handleMatch ? handleMatch[1] : '';
          const bio = node.querySelectorAll('.search-result-info p')[1]?.innerText.trim() || '';
          return { username, handle, bio };
        })
    );

    // Optionally, click the "People" tab to filter only users
    const peopleTabSelector = '.tab-btn[data-tab="people"]';
    await page.waitForSelector(peopleTabSelector, { state: 'visible' });
    await page.click(peopleTabSelector);

    // Wait for filtered user results
    await page.waitForTimeout(500); // let UI update
    await page.waitForSelector('#search-content .search-result-item', { timeout: 2000 });

    // Screenshot after clicking "People" tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract filtered user search results
    const filteredUserResults = await page.$$eval(
      '#search-content .search-result-item',
      nodes => nodes
        .filter(node => node.querySelector('.search-result-info h4'))
        .map(node => {
          const username = node.querySelector('.search-result-info h4')?.innerText.trim() || '';
          const handleLine = node.querySelector('.search-result-info p')?.innerText.trim() || '';
          const handleMatch = handleLine.match(/(@\w+)/);
          const handle = handleMatch ? handleMatch[1] : '';
          const bio = node.querySelectorAll('.search-result-info p')[1]?.innerText.trim() || '';
          return { username, handle, bio };
        })
    );

    // For verification, pick the first user result (should be "jun")
    const foundUser = filteredUserResults.find(u => u.username.toLowerCase() === 'jun');

    return {
      success: true,
      action: "Searched for users via the Search view, filtered by 'People' tab, and extracted user search results.",
      extractedData: {
        allUserResults: userResults,
        filteredUserResults,
        foundUser
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Wait for the sidebar navigation to be visible
    await page.waitForSelector('nav.sidebar ul.nav-menu');
    // Take initial screenshot
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Click the "Search" nav item using text selector (different from previous attempts)
    const searchNavItem = await page.waitForSelector('li.nav-item:has-text("Search")');
    await searchNavItem.click();

    // Wait for the search view to appear by checking for the search input with placeholder
    await page.waitForSelector('input[placeholder="Search"]#search-input', { timeout: 3000 });

    // Take screenshot after switching to search view
    const step2 = path.join(screenshotsDir, 'step_2_search_view.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Type a search query for users (e.g., "jun")
    const searchInput = await page.$('input#search-input');
    await searchInput.click();
    await searchInput.fill('jun');
    // Wait for debounce/search to process
    await page.waitForTimeout(500);

    // Wait for search results to appear (look for .search-result-item)
    await page.waitForSelector('.search-result-item', { timeout: 3000 });

    // Take screenshot of search results
    const step3 = path.join(screenshotsDir, 'step_3_search_results.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract all user search result usernames (use .search-result-info h4)
    const userResults = await page.$$eval(
      '.search-result-item .search-result-info h4',
      nodes => nodes.map(n => n.textContent.trim())
    );

    // Click the "People" tab to filter only users (use data-tab attribute)
    const peopleTab = await page.waitForSelector('button.tab-btn[data-tab="people"]');
    await peopleTab.click();
    await page.waitForTimeout(400);

    // Wait for filtered user results
    await page.waitForSelector('.search-result-item', { timeout: 2000 });

    // Take screenshot of filtered people results
    const step4 = path.join(screenshotsDir, 'step_4_people_tab.png');
    await page.screenshot({ path: step4 });
    screenshots.push(step4);

    // Extract filtered usernames again
    const filteredUserResults = await page.$$eval(
      '.search-result-item .search-result-info h4',
      nodes => nodes.map(n => n.textContent.trim())
    );

    // Click on the first user search result to open their profile
    const firstUserItem = await page.$('.search-result-item');
    if (firstUserItem) {
      await firstUserItem.click();
      // Wait for profile content to load (profile-header)
      await page.waitForSelector('.profile-header h2', { timeout: 2000 });
    }

    // Take screenshot of user profile page
    const step5 = path.join(screenshotsDir, 'step_5_user_profile.png');
    await page.screenshot({ path: step5 });
    screenshots.push(step5);

    // Extract the username and handle from the profile
    const profileUsername = await page.$eval('.profile-header h2', el => el.textContent.trim());
    const profileHandle = await page.$eval('.profile-handle', el => el.textContent.trim());

    return {
      success: true,
      action: "Searched for users, filtered by 'People', and navigated to a user's profile.",
      extractedData: {
        initialUserResults: userResults,
        filteredUserResults,
        profileUsername,
        profileHandle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Click the "Search" nav item using text selector (different from previous attempts)
    const searchNav = await page.waitForSelector('//span[text()="Search"]/parent::li', { timeout: 5000 });
    await searchNav.click();

    // Wait for the search view to be visible (by header text)
    await page.waitForSelector('//div[contains(@class,"content-header")]//input[@id="search-input"]', { timeout: 5000 });

    // Screenshot after switching to search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // 2. Type a user search query (e.g., "jun") into the search input using XPath
    const searchInput = await page.waitForSelector('//input[@id="search-input"]');
    await searchInput.fill('jun');

    // Wait for dynamic search results to load (wait for a user result to appear)
    await page.waitForSelector('//div[contains(@class,"search-result-item")]//h4[contains(text(),"jun")]', { timeout: 5000 });

    // Screenshot after search input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // 3. Extract user search results (usernames, handles, bios)
    const userResults = await page.$$eval(
      'div.search-result-item',
      nodes => nodes
        .filter(node => node.querySelector('h4'))
        .map(node => ({
          username: node.querySelector('h4')?.innerText.trim(),
          handle: (node.querySelector('p')?.innerText.match(/@\w+/) || [null])[0],
          bio: node.querySelectorAll('p')[1]?.innerText.trim() || ''
        }))
    );

    // 4. Click on the first user result (using the h4 element)
    const firstUserResult = await page.waitForSelector('//div[contains(@class,"search-result-item")]//h4[contains(text(),"jun")]', { timeout: 5000 });
    await firstUserResult.click();

    // Wait for profile view to load (wait for profile header with username "jun")
    await page.waitForSelector('//div[contains(@class,"profile-header")]//h2[text()="jun"]', { timeout: 5000 });

    // Screenshot of user profile
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_user_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_user_profile.png'));

    // Extract profile data
    const profileData = await page.evaluate(() => {
      const header = document.querySelector('.profile-header');
      if (!header) return {};
      const username = header.querySelector('h2')?.innerText.trim();
      const handle = header.querySelector('.profile-handle')?.innerText.trim();
      const bio = header.querySelector('p')?.innerText.trim();
      const stats = Array.from(header.querySelectorAll('.profile-stat')).map(el => el.innerText.trim());
      return { username, handle, bio, stats };
    });

    return {
      success: true,
      action: "Searched for users, verified search results, and navigated to a user profile.",
      extractedData: {
        userResults,
        profileData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
