import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to be loaded
    await page.waitForSelector('#threads-feed .thread-item', { timeout: 5000 });

    // Find the first thread's bookmark button
    // The first .thread-item in #threads-feed
    const firstThread = await page.$('#threads-feed .thread-item');
    if (!firstThread) throw new Error('No thread items found in feed');

    // Find the bookmark action within the first thread
    // It is: .thread-action[data-action="bookmark"]
    const bookmarkBtn = await firstThread.$('.thread-action[data-action="bookmark"]');
    if (!bookmarkBtn) throw new Error('Bookmark button not found in first thread');

    // Check if it's already bookmarked (has .bookmarked class)
    const isBookmarkedBefore = await bookmarkBtn.evaluate(el => el.classList.contains('bookmarked'));

    // Click the bookmark button if not already bookmarked
    if (!isBookmarkedBefore) {
      await bookmarkBtn.click();
      // Wait for the .bookmarked class to appear
      await page.waitForFunction(
        el => el.classList.contains('bookmarked'),
        bookmarkBtn,
        { timeout: 2000 }
      );
    }

    // Take screenshot after bookmarking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmarked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmarked.png'));

    // Extract thread content and bookmark state for verification
    const threadContent = await firstThread.$eval('.thread-text', el => el.innerText.trim());
    const isBookmarkedAfter = await bookmarkBtn.evaluate(el => el.classList.contains('bookmarked'));

    // Now go to Bookmarks view to verify it appears there
    // Click the sidebar nav-item for Bookmarks (data-view="bookmarks")
    await page.click('.nav-item[data-view="bookmarks"]');
    // Wait for bookmarks view to be active
    await page.waitForSelector('#bookmarks-view.view.active', { timeout: 3000 });

    // Wait for bookmarks feed to load
    await page.waitForSelector('#bookmarks-feed .thread-item', { timeout: 3000 });

    // Get the first thread in bookmarks
    const firstBookmarkThread = await page.$('#bookmarks-feed .thread-item');
    let bookmarkThreadContent = null;
    if (firstBookmarkThread) {
      bookmarkThreadContent = await firstBookmarkThread.$eval('.thread-text', el => el.innerText.trim());
    }

    // Take screenshot of bookmarks view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_bookmarks_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_bookmarks_view.png'));

    return {
      success: true,
      action: "Bookmarked the first thread in the feed and verified it appears in the Bookmarks view.",
      extractedData: {
        threadContent,
        isBookmarkedBefore,
        isBookmarkedAfter,
        bookmarkThreadContent,
        bookmarkVerified: threadContent === bookmarkThreadContent && isBookmarkedAfter
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];

    // Take initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the threads feed to load (using ID)
    await page.waitForSelector('#threads-feed >> .thread-item', { timeout: 5000 });

    // Find the first thread-item using nth-child CSS selector
    const firstThread = await page.$('#threads-feed .thread-item:nth-child(1)');
    if (!firstThread) throw new Error('First thread not found in feed');

    // Within the first thread, locate the bookmark action using aria-label fallback and icon
    // Try by icon: .fa-bookmark inside .thread-action
    const bookmarkBtn = await firstThread.$('.thread-actions .thread-action[data-action="bookmark"]');
    if (!bookmarkBtn) throw new Error('Bookmark button not found in first thread');

    // Check if already bookmarked (has class 'bookmarked')
    const isBookmarked = await bookmarkBtn.evaluate(el => el.classList.contains('bookmarked'));

    // Take screenshot before clicking
    const beforeBookmarkShot = path.join(screenshotsDir, 'step_2_before_bookmark.png');
    await page.screenshot({ path: beforeBookmarkShot });
    screenshots.push(beforeBookmarkShot);

    // Only click if not already bookmarked
    if (!isBookmarked) {
      await bookmarkBtn.click();
      // Wait for the class to update to 'bookmarked'
      await page.waitForFunction(
        el => el.classList.contains('bookmarked'),
        bookmarkBtn,
        { timeout: 2000 }
      );
    }

    // Take screenshot after clicking
    const afterBookmarkShot = path.join(screenshotsDir, 'step_3_after_bookmark.png');
    await page.screenshot({ path: afterBookmarkShot });
    screenshots.push(afterBookmarkShot);

    // Extract thread text for verification
    const threadText = await firstThread.$eval('.thread-text', el => el.textContent.trim());

    // Extract bookmark state after click
    const isNowBookmarked = await bookmarkBtn.evaluate(el => el.classList.contains('bookmarked'));

    // Optionally, check for toast notification (success)
    let toastText = null;
    try {
      await page.waitForSelector('.toast.toast-success', { timeout: 1500 });
      toastText = await page.$eval('.toast.toast-success .toast-message', el => el.textContent.trim());
    } catch (e) {
      // Toast may disappear quickly; ignore if not found
    }

    return {
      success: true,
      action: "Bookmarked the first thread in the feed using a robust selector strategy.",
      extractedData: {
        threadText,
        wasInitiallyBookmarked: isBookmarked,
        isNowBookmarked,
        toastText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

